import{_ as p,c as e,o as t,e as a}from"./app.5bdfebe0.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u5BF9\u8BDD20\u5E74\u7ECF\u9A8C\u7403\u978B\u85CF\u5BB6\uFF1A\u201C\u7092\u978B\u201D\u51F6\u731B \u201C\u9570\u5200\u201D\u6765\u88AD","slug":"title-\u5BF9\u8BDD20\u5E74\u7ECF\u9A8C\u7403\u978B\u85CF\u5BB6\uFF1A\u201C\u7092\u978B\u201D\u51F6\u731B-\u201C\u9570\u5200\u201D\u6765\u88AD","link":"#title-\u5BF9\u8BDD20\u5E74\u7ECF\u9A8C\u7403\u978B\u85CF\u5BB6\uFF1A\u201C\u7092\u978B\u201D\u51F6\u731B-\u201C\u9570\u5200\u201D\u6765\u88AD","children":[]}],"relativePath":"1-100\u671F/_doc/14-2.md","lastUpdated":1667549945000}'),r={name:"1-100\u671F/_doc/14-2.md"},i=a('<hr><h2 id="title-\u5BF9\u8BDD20\u5E74\u7ECF\u9A8C\u7403\u978B\u85CF\u5BB6\uFF1A\u201C\u7092\u978B\u201D\u51F6\u731B-\u201C\u9570\u5200\u201D\u6765\u88AD" tabindex="-1">title: \u5BF9\u8BDD20\u5E74\u7ECF\u9A8C\u7403\u978B\u85CF\u5BB6\uFF1A\u201C\u7092\u978B\u201D\u51F6\u731B \u201C\u9570\u5200\u201D\u6765\u88AD <a class="header-anchor" href="#title-\u5BF9\u8BDD20\u5E74\u7ECF\u9A8C\u7403\u978B\u85CF\u5BB6\uFF1A\u201C\u7092\u978B\u201D\u51F6\u731B-\u201C\u9570\u5200\u201D\u6765\u88AD" aria-hidden="true">#</a></h2><p><a href="https://baijiahao.baidu.com/s?id=1696910695488689520&amp;wfr=spider&amp;for=pc" target="_blank" rel="noreferrer">https://baijiahao.baidu.com/s?id=1696910695488689520&amp;wfr=spider&amp;for=pc</a></p><p>\u672C\u671F\u8BDD\u9898\uFF1A\u5343\u5143\u56FD\u6F6E\u7403\u978B\u88AB\u7092\u81F3\u6570\u4E07\u5143\uFF0C\u5F15\u53D1\u201C\u7834\u5708\u201D\u5173\u6CE8\u3002\u80CC\u540E\u662F\u4E2A\u6848\uFF0C\u8FD8\u662F\u666E\u904D\u73B0\u8C61\uFF1F\u7403\u978B\u5382\u5546\u3001\u7403\u978B\u4EA4\u6613\u5E73\u53F0\u3001\u9EC4\u725B\u515A\uFF0C\u8C01\u662F\u5E55\u540E\u63A8\u624B\uFF1F\u4ECE\u76F2\u76D2\u5230\u7403\u978B\uFF0C\u5230\u5E95\u662F\u201C\u5C0F\u4F17\u6587\u5316\u201D\u7684\u4EF7\u503C\u6536\u85CF\uFF0C\u8FD8\u662F\u5229\u7528\u201C\u8D22\u5BCC\u7126\u8651\u75C7\u201D\u5272\u97ED\u83DC\uFF1F\u7403\u978B\u6536\u85CF\u5E02\u573A\u6709\u600E\u6837\u7684\u201C\u996D\u5708\u201D\u6587\u5316\uFF1F\u5F97\u7269\u3001NICE\u7B49\u5934\u90E8\u7403\u978B\u4EA4\u6613\u5E73\u53F0\uFF0C\u5728\u4EA7\u4E1A\u94FE\u4E2D\uFF0C\u626E\u6F14\u7740\u600E\u6837\u7684\u89D2\u8272\uFF1F\u201C\u7403\u978B\u4E00\u9762\u5899\uFF0C\u582A\u6BD4\u4E00\u5957\u623F\u201D\u7684\u8D22\u5BCC\u6548\u5E94\u4E0B\uFF0C\u4E00\u65B9\u9762\uFF0C\u8981\u5C0A\u91CD\u5E02\u573A\u7684\u201C\u4EA4\u6613\u81EA\u7531\u201D\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u8981\u9632\u6B62\u201C\u5E84\u5BB6\u201D\u5272\u97ED\u83DC\u7684\u98CE\u9669\u3002\u4E8C\u8005\u5982\u4F55\u5E73\u8861\uFF0C\u624D\u8BA9\u201C\u978B\u7A7F\u4E0D\u7092\u201D\uFF1F</p><p>\u574A\u95F4\u66FE\u8BF4\uFF0C\u201C70\u540E\u7092\u623F\uFF0C80\u540E\u7092\u80A1\uFF0C90\u540E\u7092\u5E01\uFF0C00\u540E\u7092\u978B\u3002\u201D\u201C\u7403\u978B\u4E00\u9762\u5899\uFF0C\u582A\u6BD4\u4E00\u5957\u623F\u3002\u201D</p><p>\u636E\u62A5\u9053\uFF0C 2019\u5E748\u670819\u65E5\uFF0C26\u6B3E\u70ED\u95E8\u7403\u978B\u5355\u65E5\u6210\u4EA4\u603B\u989D\u7A81\u78344.5\u4EBF\u5143\uFF0C\u8D85\u8FC7\u540C\u65E5\u65B0\u4E09\u677F9431\u5BB6\u516C\u53F8\u6210\u4EA4\u91CF\u3002\u6700\u8FD1\uFF0C\u4E00\u80A1\u6253\u7740\u652F\u6301\u56FD\u8D27\u540D\u53F7\u7684\u201C\u7092\u978B\u6F6E\u201D\u5907\u53D7\u5173\u6CE8\uFF1A\u5B89\u8E0F\u3001\u674E\u5B81\u3001\u56DE\u529B\u7B49\u56FD\u5185\u54C1\u724C\u7403\u978B\u4EF7\u683C\u6C34\u6DA8\u8239\u9AD8\uFF0C\u5728\u77E5\u540D\u7403\u978B\u4EA4\u6613\u5E73\u53F0\u5F97\u7269APP\u4E0A\uFF0C\u4E00\u53CC\u5E02\u573A\u53C2\u8003\u552E\u4EF71499\u5143\u7684\u674E\u5B81\u201C\u97E6\u5FB7\u4E4B\u90534\u201D\u660E\u661F\u9650\u91CF\u6B3E\u7403\u978B\u88AB\u7092\u81F348889\u5143\u3002\u88AB\u5A92\u4F53\u66DD\u5149\u540E\uFF0C\u5F97\u7269\u5E73\u53F0\u5B98\u65B9\u56DE\u5E94\u201C\u7CFB\u5356\u5BB6\u5355\u65B9\u9762\u6807\u4EF7\u201D\uFF0C\u5DF2\u5C06\u591A\u6B3E\u4EF7\u683C\u4E25\u91CD\u504F\u79BB\u5E02\u573A\u53C2\u8003\u4EF7\u7684\u7403\u978B\u6682\u65F6\u4E0B\u67B6\u3002</p><p><img src="https://pics6.baidu.com/feed/faedab64034f78f0f8889d5c27445d5db2191c1e.jpeg%EF%BC%9Ftoken=3b4145ece3a411216b0c9c139ace874b" alt=""></p><p>\u4E3B\u6301\u4EBA\u674E\u7A0B\uFF08\u4E0A\u5DE6\uFF09\uFF0C\u7ECF\u6D4E\u4E4B\u58F0\u89C2\u5BDF\u5458\u3001\u300A\u8FDC\u89C1\u300B\u680F\u76EE\u5236\u4F5C\u4EBA\u601D\u8FDC\uFF08\u4E0A\u53F3\uFF09\uFF0C\u7ECF\u6D4E\u4E4B\u58F0\u89C2\u5BDF\u5458\u6881\u5A67\uFF08\u4E0B\u5DE6\uFF09\uFF0C20\u5E74\u7ECF\u9A8C\u8D44\u6DF1\u7403\u978B\u6536\u85CF\u8005\u5F20\u4EAE\uFF08\u4E0B\u53F3\uFF09</p><p>\u56FD\u6F6E\u7403\u978B \u7A81\u5982\u5176\u6765\u7684\u201C\u706B\u7BAD\u884C\u60C5\u201D</p><p>\u674E\u7A0B\uFF1A\u201C\u7092\u978B\u201D\u662F\u8FD9\u4E24\u5E74\u5A92\u4F53\u53CD\u590D\u63D0\u5230\uFF0C\u751A\u81F3\u6279\u8BC4\u7684\u73B0\u8C61\u3002\u4E4B\u6240\u4EE5\u8FD9\u6B21\u201C\u7834\u5708\u201D\u5173\u6CE8\uFF0C\u662F\u56E0\u4E3A\u201C\u5750\u706B\u7BAD\u201D\u7684\u4E3B\u89D2\u4ECE\u6D77\u5916\u54C1\u724C\u53D8\u6210\u4E86\u56FD\u4EA7\u54C1\u724C\u3002\u56E0\u4E3A\u5927\u5BB6\u5BF9\u56FD\u6F6E\u7684\u5370\u8C61\u5E94\u8BE5\u662F\u201C\u4E3B\u6253\u6027\u4EF7\u6BD4\u201D\u201C\u7269\u7F8E\u4EF7\u5EC9\u201D\uFF0C\u8FD9\u6CE2\u201C\u706B\u7BAD\u884C\u60C5\u201D\u662F\u201C\u7A81\u53D1\u60C5\u51B5\u201D\uFF0C\u8FD8\u662F\u4E00\u76F4\u5B58\u5728\uFF1F\u662F\u4E2A\u6848\uFF0C\u8FD8\u662F\u666E\u904D\u73B0\u8C61\uFF1F</p><p>\u601D\u8FDC\uFF1A\u201C\u4EE5\u524D\u662F\u6CA1\u94B1\u7A7F\u674E\u5B81\uFF0C\u73B0\u5728\u662F\u6CA1\u94B1\u7A7F\u674E\u5B81\u201D\u3002\u76F8\u6BD4\u7092\u77F3\u5934\u3001\u7092\u7389\u3001\u7092\u80A1\u7968\uFF0C\u4E00\u8BF4\u201C\u7092\u978B\u201D\uFF0C\u8206\u8BBA\u5448\u73B0\u51FA\u201C\u5BB6\u957F\u5F0F\u201D\u7126\u8651\uFF0C\u4E3A\u4EC0\u4E48\uFF1F\u4E00\u662F\u73A9\u978B\u548C\u201C\u7092\u978B\u201D\u7684\u5E74\u8F7B\u4EBA\u5C45\u591A\u3002\u4E8C\u662F\u6BCF\u6B21\u201C\u7092\u978B\u6F6E\u201D\uFF0C\u4EF7\u683C\u80CC\u79BB\u786E\u5B9E\u6BD4\u8F83\u4E25\u91CD\u3002</p><p>\u5F20\u4EAE\uFF1A\u7403\u978B\u5E02\u573A\u53D1\u5C55\u81F3\u4ECA\uFF0C\u7403\u978B\u5728\u5F88\u591A\u5730\u65B9\u88AB\u5B9A\u4E49\u4E3A\u4F53\u80B2\u7C7B\u6536\u85CF\u54C1\uFF0C\u6240\u4EE5\u624D\u6709\u201C\u7092\u201D\u7684\u4EF7\u503C\u3002\u8FD9\u6B21\u201C\u7092\u978B\u201D\u98CE\u6CE2\uFF0C\u9664\u4E86\u8DDF\u4E00\u4E9B\u654F\u611F\u4E8B\u4EF6\u6709\u5173\uFF0C\u672C\u8EAB\u201C\u97E6\u5FB7\u4E4B\u90534\u201D\u662F\u5168\u660E\u661F\u9650\u91CF\u6B3E\uFF0C\u53D1\u552E\u6570\u91CF\u975E\u5E38\u5C11\uFF0C\u73B0\u5728\u8FD9\u6B3E\u978B\u5DF2\u51FA\u5230\u4E86\u4E03\u516B\u4EE3\u3002\u8FD9\u4E2A\u978B\u6B3E\u786E\u5B9E\u6709\u4EE3\u8868\u6027\uFF0C\u4F46\u76EE\u524D\u4EF7\u683C\u8FC7\u9AD8\u4E86\u3002</p><p>\u6881\u5A67\uFF1A\u6211\u8BA4\u8BC6\u7684\u7403\u978B\u5356\u5BB6\u8BF4\uFF0C\u4EE5\u524D\u88AB\u7206\u7092\u7684\u6930\u5B50\uFF08YEZZY\uFF09\u7403\u978B\u73B0\u5728\u4E24\u53CC\u624D\u8D5A600\u5143\uFF0C\u5728\u5F53\u524D\u884C\u60C5\u4E0B\u5DF2\u7ECF\u7B97\u4E0D\u9519\u4E86\u3002\u7403\u978B\u5E02\u573A\u4EE5\u524D\u662F\u201C\u5C0F\u706B\u6162\u7096\u201D\u72B6\u6001\uFF0C\u73B0\u5728\u7A81\u7136\u95F4\u201C\u6025\u706B\u7206\u7092\u201D\u4E86\u4E00\u4E0B\u3002</p><p>\u674E\u7A0B\uFF1A\u7403\u978B\u7406\u8BBA\u4E0A\u53EF\u4EE5\u65E0\u9650\u751F\u4EA7\uFF0C\u4E3A\u4EC0\u4E48\u63A7\u4EA7\uFF1F</p><p>\u5F20\u4EAE\uFF1A\u5927\u5BB6\u4F4E\u4F30\u4E86\u4E16\u754C\u5E02\u573A\u5BF9\u7403\u978B\u6D88\u5316\u7684\u80FD\u529B\uFF0C\u589E\u52A0\u4EA7\u91CF\u662F\u6709\u98CE\u9669\u7684\uFF0C\u4F1A\u964D\u4F4E\u54C1\u724C\u7684\u5355\u54C1\u4EF7\u503C\u3002\u4F53\u80B2\u85CF\u54C1\u672C\u6765\u5C31\u662F\u7269\u4EE5\u7A00\u4E3A\u8D35\u3002</p><p>\u601D\u8FDC\uFF1A\u7403\u978B\u4E5F\u6709\u201C\u5496\u4F4D\u201D\u3002\u6536\u85CF\u7403\u978B\u5E76\u975E\u201C\u662F\u978B\u5C31\u85CF\u201D\uFF0C\u6BCF\u53CC\u978B\u6709\u5C5E\u4E8E\u81EA\u5DF1\u7684\u6545\u4E8B\u3002\u4E00\u662F\u627F\u8F7D\u7740\u978B\u8FF7\u7684IP\u56DE\u5FC6\u548C\u6545\u4E8B\uFF1B\u4E8C\u662F\u5236\u978B\u5DE5\u827A\u4E0A\uFF0C\u786E\u5B9E\u8D4B\u4E88\u85CF\u978B\u7F8E\u611F\u548C\u6536\u85CF\u4EF7\u503C\uFF1B\u4E09\u662F\u6700\u5F00\u59CB\u978B\u8FF7\u662F\u901A\u8FC7\u978B\u627E\u5230\u5171\u540C\u7684\u793E\u4EA4\u8BED\u8A00\u3001\u793E\u4EA4\u8D27\u5E01\u3002</p><p>\u7403\u978B\u7684\u201C\u996D\u5708\u6587\u5316\u201D</p><p>\u674E\u7A0B\uFF1A\u7403\u978B\u6536\u85CF\u5230\u5E95\u662F\u6709\u4EF7\u503C\uFF0C\u8FD8\u662F\u5272\u97ED\u83DC\uFF0C\u5C31\u8981\u4E86\u89E3\u8FD9\u4E2A\u5708\u5B50\u548C\u6587\u5316\u3002\u5F20\u4EAE\u7ED9\u5927\u5BB6\u4ECB\u7ECD\u4E00\u4E0B\u3002</p><p>\u5F20\u4EAE\uFF1A\u7403\u978B\u6587\u5316\u5728\u4E2D\u56FD90\u5E74\u4EE3\u672B\u5C31\u8BDE\u751F\u4E86\uFF0C2004\u5E74\u5DE6\u53F3\u56FD\u5185\u51FA\u73B0\u7B2C\u4E00\u672C\u7403\u978B\u6742\u5FD7\uFF0C\u7B97\u662F\u201C\u91CC\u7A0B\u7891\u201D\uFF0C\u540E\u6765\u559C\u6B22\u7684\u4EBA\u8D8A\u6765\u8D8A\u591A\uFF0C\u52A0\u4E0A\u4E00\u4E9B\u6587\u5316\u7EFC\u827A\u7684\u5BA3\u4F20\uFF0C\u8FD9\u662F\u63A8\u52A8\u7403\u978B\u53D1\u5C55\u7684\u91CD\u8981\u73AF\u8282\uFF0C\u8BA9\u7403\u978B\u4ECE\u201C\u7403\u8FF7\u5708\u201D\u7834\u5708\u8FDB\u5165\u201C\u6F6E\u6D41\u6587\u5316\u201D\u4E2D\uFF0C\u53D7\u5404\u79CD\u56E0\u7D20\u5F71\u54CD\uFF0C\u9700\u6C42\u91CF\u53D8\u5927\uFF0C\u54C1\u7C7B\u66F4\u4E30\u5BCC\uFF0C\u4EF7\u683C\u6C34\u6DA8\u8239\u9AD8\u3002</p><p>\u674E\u7A0B\uFF1A\u4EE5\u524D\u8BF4\u8D77\u56FD\u4EA7\u978B\uFF0C\u5C31\u662F\u201C\u4FBF\u5B9C\u3001\u5B9E\u60E0\u3001\u8010\u9020\u201D\u3002\u5982\u4ECA\uFF0C\u4F34\u968F\u7740\u201C\u56FD\u6F6E\u201D\u8FD9\u4E2A\u8BCD\u7684\u5174\u8D77\uFF0C\u5F88\u591A\u56FD\u4EA7\u7403\u978B\u54C1\u724C\u4E5F\u65E7\u8C8C\u6362\u65B0\u989C\u3002\u600E\u4E48\u770B\u56FD\u4EA7\u54C1\u724C\u7403\u978B\u7684\u73B0\u72B6\uFF1F\u201C\u7092\u978B\u201D\u98CE\u6CE2\uFF0C\u56FD\u4EA7\u978B\u662F\u4E0D\u662F\u201C\u4E50\u89C1\u5176\u6210\u201D\uFF0C\u751A\u81F3\u53C2\u4E0E\u5176\u4E2D\u4E86\uFF1F</p><p>\u601D\u8FDC\uFF1A10\u5E74\u524D\uFF0C\u56FD\u5185\u4F53\u80B2\u54C1\u724C\u548C\u56FD\u5916\u54C1\u724C\u7684\u5DEE\u8DDD\u662F\u5168\u65B9\u4F4D\u7684\u2014\u2014\u5305\u62EC\u4EA7\u54C1\u8BBE\u8BA1\u3001\u4EF7\u683C\u3001\u8425\u9500\u65B9\u5F0F\u3001\u5E7F\u544A\u8C03\u6027\u3001\u4EE3\u8A00\u4EBA\uFF0C\u548C\u7531\u6B64\u5F62\u6210\u7684\u201C\u54C1\u724C\u6C14\u8D28\u201D\u3002\u5C24\u5176\u662F\u4EE3\u8A00\u4EBA\uFF0C\u56FD\u9645\u54C1\u724C\u53EA\u7B7ENBA\u5934\u90E8\u7403\u661F\uFF0C\u56FD\u4EA7\u54C1\u724C\u6700\u591A\u662F\u666E\u901A\u7403\u961F\u7684\u4E3B\u529B\u6216\u8C6A\u95E8\u7684\u8FB9\u7F18\u7403\u5458\u3002\u65E9\u5728\u51E0\u5E74\u524D\uFF0C\u56FD\u4EA7\u4EE3\u8A00\u4EBA\u7684\u201C\u5496\u4F4D\u201D\u660E\u663E\u4E0A\u6765\u4E86\uFF0C\u81F3\u5C11\u662F\u5927\u5BB6\u516C\u8BA4\u7684\u660E\u661F\u7403\u5458\u4E86\u3002\u5F53\u7136\uFF0C\u56FD\u6F6E\u7403\u978B\u54C1\u724C\u7684\u6C14\u8D28\u3001\u4EF7\u683C\u4E5F\u53D8\u4E86\u3002</p><p>\u5F20\u4EAE\uFF1A\u56FD\u4EA7\u7403\u978B\u54C1\u724C\u6574\u4F53\u53D1\u5C55\u5DF2\u7ECF\u6BD4\u8F83\u6210\u719F\u3002\u56E0\u4E3A\u5B83\u4EEC\u66F4\u52A0\u76F8\u4FE1\u5E74\u8F7B\u4EBA\u5BF9\u5E02\u573A\u7684\u628A\u63E1\u548C\u654F\u9510\u5EA6\uFF0C\u4E5F\u80FD\u6293\u4F4F\u8FD9\u6CE2\u52A9\u529B\u6D88\u8D39\u7FA4\u4F53\u7684\u559C\u597D\u548C\u8C03\u6027\u3002</p><p>\u6881\u5A67\uFF1A\u817E\u8BAF\u793E\u4EA4\u5E7F\u544A\u66FE\u505A\u4E86\u4E2A\u5927\u6570\u636E\u8C03\u67E5\u2014\u201400\u540E\u4E2D85%\u4E0D\u8BA4\u4E3A\u56FD\u5916\u54C1\u724C\u4F18\u4E8E\u56FD\u4EA7\u54C1\u724C\u3002\u4ED6\u4EEC\u5BF9\u56FD\u6F6E\u54C1\u724C\u8FD8\u662F\u76F8\u5F53\u6709\u81EA\u4FE1\u3002</p><p>\u7403\u978B\u4EA4\u6613\u5E73\u53F0\u7684\u5C34\u5C2C\u4E0E\u6311\u6218\uFF1A\u65E2\u5F53\u9009\u624B\uFF0C\u53C8\u5F53\u88C1\u5224</p><p>\u674E\u7A0B\uFF1A\u7403\u978B\u4EA4\u6613\u5E73\u53F0\u76EE\u524D\u662F\u5982\u4F55\u8FD0\u4F5C\u3001\u76C8\u5229\u7684\uFF1F</p><p>\u5F20\u4EAE\uFF1A\u76C8\u5229\u5305\u62EC\u51E0\u5927\u6838\u5FC3\u677F\u5757\u2014\u2014\u5E73\u53F0\u7528\u6237\u7684\u7403\u978B\u4EA4\u6613\u4E2D\u201C\u62BD\u6C34\u201D\uFF0C\u4E5F\u53EB\u4F63\u91D1\uFF1B\u8FD8\u6709\u4E00\u90E8\u5206\u662F\u81EA\u8425\uFF0C\u5305\u62EC\u7279\u6B8A\u6B3E\u72EC\u5BB6\u53D1\u552E\u7684\u6E20\u9053\uFF1B\u53E6\u5916\u662F\u501F\u52A9\u5927\u6D41\u91CF\uFF0C\u5728\u5F00\u5C4F\u3001\u89C6\u9891\u6216\u8005\u793E\u533A\u5185\u7684\u63A8\u9001\u4E2D\u505A\u5E7F\u544A\uFF0C\u6700\u540E\u4E00\u4E2A\u677F\u5757\u5C31\u662F\u53D1\u552E\u72EC\u5BB6\u5468\u8FB9\u4EA7\u54C1\u3002</p><p>\u601D\u8FDC\uFF1A\u9274\u5B9A\u8FD8\u6536\u94B1\u5417\uFF1F</p><p>\u5F20\u4EAE\uFF1A\u9274\u5B9A\u90E8\u5206\u6536\u8D39\uFF0C\u6709\u514D\u8D39\u9274\u5B9A\uFF0C\u4E5F\u6709\u4ED8\u8D39\u9274\u5B9A\uFF0C\u4F46\u662F\u9274\u5B9A\u5DF2\u7ECF\u4E0D\u7B97\u662F\u6838\u5FC3\u76C8\u5229\u6765\u6E90\uFF0C\u6700\u5927\u5934\u662F\u5728\u5E73\u53F0\u4E0A\u9500\u552E\u7403\u978B\u7684\u4FDD\u8BC1\u91D1\u3002\u5728\u7403\u978B\u5B8C\u6210\u9500\u552E\u6D41\u7A0B\u4E4B\u524D\uFF0C\u4F1A\u6709\u76F8\u5F53\u5927\u6570\u989D\u7684\u4FDD\u8BC1\u91D1\u7559\u5B58\u5728\u5E73\u53F0\u4E0A\uFF0C\u5BF9\u4E8E\u4E00\u4E2A\u5E74\u6D41\u6C34\u8FC7\u4EBF\u7684\u5E73\u53F0\uFF0C\u8FD9\u662F\u4E00\u7B14\u4E0D\u5C0F\u7684\u6570\u5B57\u3002</p><p>\u601D\u8FDC\uFF1A\u4F46\u5E73\u53F0\u662F\u4E0D\u662F\u65E2\u5F53\u8FD0\u52A8\u5458\uFF0C\u53C8\u5F53\u88C1\u5224\u5458\uFF1F\u201C\u7092\u978B\u201D\u4E8B\u4EF6\u51FA\u6765\u540E\uFF0C\u7F51\u4E0A\u8D28\u7591\u5E73\u53F0\u6709 \u201C\u5DE6\u624B\u5012\u53F3\u624B\u201D\u7684\u5ACC\u7591\u3002\u56E0\u4E3A\u7406\u8BBA\u4E0A\uFF0C\u6210\u4E3A\u884C\u4E1A\u98CE\u5411\u6807\u7684\u5E73\u53F0\uFF0C\u5728\u4E8C\u7EA7\u5E02\u573A\u62E5\u6709\u91CD\u8981\u7684\u5B9A\u4EF7\u6743\uFF0C\u53EF\u4EE5\u5229\u7528\u6D88\u8D39\u8005\u7684\u201C\u4FE1\u606F\u5DEE\u201D\u6536\u5272\u4E00\u90E8\u5206\u201C\u97ED\u83DC\u201D\u3002</p><p>\u5F20\u4EAE\uFF1A\u5F88\u591A\u4EBA\u6709\u8FD9\u6837\u7684\u8BC4\u4EF7\u3002\u5E73\u5FC3\u800C\u8BBA\uFF0C\u8FD9\u4E9B\u5E73\u53F0\u7684\u552E\u540E\u670D\u52A1\u786E\u5B9E\u9700\u8981\u8FDB\u4E00\u6B65\u52A0\u5F3A\uFF0C\u867D\u7136\u73B0\u5728\u6709\u6574\u6539\u63AA\u65BD\uFF0C\u4F46\u8FD9\u65B9\u9762\u8FD8\u6709\u5F85\u52A0\u5F3A\u3002\u8FD9\u4E2A\u5C40\u9762\u6050\u6015\u4F1A\u5B58\u5728\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\u3002\u600E\u4E48\u529E\uFF1F\u6D88\u8D39\u8005\u7684\u6D88\u8D39\u6559\u80B2\u662F\u91CD\u8981\u51FA\u53D1\u70B9\uFF0C\u6BD4\u5982\u8981\u63D0\u5021\u7406\u6027\u6D88\u8D39\u3001\u628A\u201C\u7092\u978B\u201D\u7684\u6982\u5FF5\u964D\u4E0B\u6765\uFF0C\u66F4\u591A\u5021\u5BFC\u7403\u978B\u7684\u6587\u5316\u4E0E\u5185\u6DB5\u3002</p><p>\u6881\u5A67\uFF1A\u7403\u978B\u5708\u4E5F\u6709\u201C\u4E8C\u516B\u539F\u5219\u201D\uFF0C\u6BD2APP2019\u5E74\u7684\u6570\u636E\u663E\u793A\uFF0C88%\u7684\u666E\u901A\u5356\u5BB6\u8D21\u732E\u4E8654%\u7684\u4EA4\u6613\u989D\uFF0C12%\u7684\u4E13\u4E1A\u73A9\u5BB6\u8D21\u732E\u4E86\u51E0\u4E4E\u6301\u5E73\u7684\u4EA4\u6613\u989D\uFF0C\u5360\u6BD446%\u3002\u8FD9\u4E5F\u8BF4\u660E\uFF0C\u4E13\u4E1A\u73A9\u5BB6\u7684\u8206\u8BBA\u8BDD\u8BED\u6743\u3001\u5546\u54C1\u8D44\u6E90\u5784\u65AD\u8D8A\u6765\u8D8A\u96C6\u4E2D\uFF0C\u53EF\u4EE5\u5E2E\u5E73\u53F0\u6C89\u6DC0\u7528\u6237\u548C\u6D41\u91CF\uFF0C\u5F53\u201C\u7092\u978B\u201D\u6E38\u620F\u5F00\u573A\uFF0C\u54E8\u58F0\u4E00\u54CD\uFF0C\u88C1\u5224\u4F1A\u600E\u6837\u6447\u6446\uFF1F</p><p>\u5E74\u8F7B\u7FA4\u4F53\u7684\u201C\u8D22\u5BCC\u7126\u8651\u75C7\u201D \u5982\u4F55\u5F15\u5BFC\u201C\u978B\u7A7F\u4E0D\u7092\u201D\uFF1F</p><p>\u674E\u7A0B\uFF1A\u73B0\u5728\u5F88\u591A\u4EBA\uFF0C\u5C24\u5176\u662F\u5E74\u8F7B\u4EBA\uFF0C\u60A3\u4E0A\u4E86\u201C\u8D22\u5BCC\u7126\u8651\u75C7\u201D\u3002\u4ECE\u6BD4\u7279\u5E01\u5230\u76F2\u76D2\u518D\u5230\u7403\u978B\uFF0C\u8FD9\u4E48\u591A\u4EBA\u5230\u5708\u5B50\u91CC\u6765\uFF0C\u5F20\u4EAE\uFF0C\u4F60\u662F\u9AD8\u5174\u8FD8\u662F\u62C5\u5FE7\uFF1F\u4ECE\u5E02\u573A\u89D2\u5EA6\uFF0C\u6D88\u8D39\u8005\u548C\u5546\u5BB6\u662F\u4EA4\u6613\u81EA\u7531\uFF0C\u4F46\u4ECE\u201C\u978B\u7A7F\u4E0D\u7092\u201D\u548C\u9632\u6B62\u5E74\u8F7B\u4EBA\u201C\u6295\u8D44\u7126\u8651\u201D\u76F2\u76EE\u8DDF\u98CE\u3001\u9632\u6B62\u201C\u6076\u610F\u5750\u5E84\u3001\u5272\u97ED\u83DC\u201D\u7684\u89D2\u5EA6\uFF0C\u4E3B\u7BA1\u90E8\u95E8\u662F\u5426\u5E94\u8BE5\u8FDB\u884C\u4E00\u5B9A\u7684\u9650\u5236\u548C\u89C4\u8303\uFF1F</p><p>\u5F20\u4EAE\uFF1A\u5E74\u8F7B\u4EBA\u5BF9\u8D22\u5BCC\u7684\u9700\u6C42\u80AF\u5B9A\u6709\uFF0C\u7126\u8651\u75C7\u4E5F\u6709\uFF0C\u4F46\u53D7\u9650\u4E8E\u8D22\u5BCC\u83B7\u5F97\u6E20\u9053\u548C\u4E13\u4E1A\u7406\u8D22\u7684\u9AD8\u95E8\u69DB\uFF0C\u4ECE\u8EAB\u8FB9\u7684\u5174\u8DA3\u7231\u597D\u548C\u81EA\u5DF1\u6240\u64C5\u957F\u7684\u9886\u57DF\u4E0B\u624B\u6210\u4E3A\u4ED6\u4EEC\u8D5A\u94B1\u7684\u624B\u6BB5\u3002\u5F88\u591A\u5E74\u8F7B\u4EBA\u5728\u5927\u4F53\u80B2\u6587\u5316\u4E2D\u6210\u957F\uFF0C\u5F53\u53D1\u73B0\u5174\u8DA3\u7231\u597D\u53EF\u4EE5\u8D5A\u94B1\uFF0C\u5F88\u591A\u4EBA\u4E5F\u5C31\u6162\u6162\u6295\u5165\u5176\u4E2D\u3002</p><p>\u601D\u8FDC\uFF1A95\u540E\u300100\u540E\u6709\u4E13\u5C5E\u7684\u90E8\u843D\u5316\u601D\u7EF4\u548C\u4EA4\u6D41\u65B9\u5F0F\uFF0C\u867D\u7136\u5728\u8D22\u5BCC\u7126\u8651\u75C7\u7684\u5F71\u54CD\u4E0B\u5F88\u591A\u4EBA\u4F1A\u505A\u5404\u79CD\u4E0D\u540C\u7684\u5C1D\u8BD5\uFF0C\u4F46\u5B9E\u9645\u4E0A\u4ED6\u4EEC\u7684\u6297\u98CE\u9669\u80FD\u529B\u6BD4\u8F83\u5F31\u3002\u56E0\u6B64\u76F8\u5173\u4E3B\u7BA1\u90E8\u95E8\u8981\u9632\u6B62\u6076\u610F\u5E84\u5BB6\u5272\u97ED\u83DC\u3002</p><p>\u4F46\u603B\u4F53\u4E0A\uFF0C\u9996\u5148\u76D1\u7BA1\u5BF9\u978B\u5708\u6765\u8BF4\uFF0C\u672A\u6765\u66F4\u591A\u662F\u201C\u88AB\u52A8\u6216\u8865\u6551\u6027\u201D\u7684\u3002\u978B\u5708\u8FD8\u662F\u5C0F\u4F17\u5E02\u573A\uFF0C\u53EA\u6709\u51FA\u73B0\u6781\u5EA6\u5938\u5F20\u548C\u8D1F\u9762\u5F71\u54CD\u7684\u60C5\u51B5\uFF0C\u76D1\u7BA1\u624D\u4F1A\u4ECB\u5165\uFF1B\u5176\u6B21\u672A\u6765\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u7528\u6280\u672F\u4F5C\u4E3A\u91CD\u8981\u7684\u76D1\u7BA1\u548C\u89C4\u8303\u624B\u6BB5\uFF0C\u6BD5\u7ADF\u662F\u7535\u5B50\u5546\u52A1\u4EA4\u6613\uFF0C\u6211\u56FD\u73B0\u5728\u7684\u7535\u5B50\u5546\u52A1\u6CD5\u3001\u6D88\u8D39\u8005\u6743\u76CA\u4FDD\u62A4\u6CD5\u3001\u53CD\u5784\u65AD\u6CD5\uFF0C\u660E\u786E\u89C4\u5B9A\u4E86\u5E73\u53F0\u7684\u8D23\u4EFB\uFF0C\u5BF9\u5E73\u53F0\u4EA4\u6613\u884C\u4E3A\u8981\u201C\u53EF\u6EAF\u6E90\u3001\u53EF\u8FFD\u67E5\u201D\u3002\u5982\u6709\u5927\u6570\u636E\u6740\u719F\u3001\u5DE6\u624B\u5012\u53F3\u624B\u201C\u5237\u5355\u201D\u7092\u4F5C\u7684\u884C\u4E3A\uFF0C\u4E00\u7ECF\u53D1\u73B0\uFF0C\u4E88\u4EE5\u91CD\u7F5A\u3002</p><p>\u6881\u5A67\uFF1A\u91D1\u878D\u76D1\u7BA1\u4E0A\u8981\u4E0B\u72E0\u624B\u3002\u5728\u6574\u4E2A\u201C\u7092\u978B\u201D\u94FE\u6761\u4E2D\uFF0C\u91D1\u878D\u673A\u6784\u63D0\u4F9B\u6760\u6746\u5F0F\u7684\u8D44\u91D1\uFF0C\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\u50AC\u751F\u201C\u7092\u978B\u201D\u6CE1\u6CAB\u3002</p>',37),c=[i];function d(o,s,_,n,l,f){return t(),e("div",null,c)}const b=p(r,[["render",d]]);export{m as __pageData,b as default};
