import{_ as p,c as e,o as t,e as a}from"./app.1378663d.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u8D70\u5411\u5168\u56FD","slug":"title-\u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u8D70\u5411\u5168\u56FD","link":"#title-\u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u8D70\u5411\u5168\u56FD","children":[{"level":3,"title":"\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u73B0\u573A\u4F1A\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C","slug":"\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u73B0\u573A\u4F1A\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C","link":"#\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u73B0\u573A\u4F1A\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C","children":[]}]}],"relativePath":"1-100\u671F/_doc/22-1.md","lastUpdated":1665416314000}'),r={name:"1-100\u671F/_doc/22-1.md"},n=a('<hr><h2 id="title-\u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u8D70\u5411\u5168\u56FD" tabindex="-1">title: \u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u8D70\u5411\u5168\u56FD <a class="header-anchor" href="#title-\u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u8D70\u5411\u5168\u56FD" aria-hidden="true">#</a></h2><p><a href="https://news.southcn.com/node_54a44f01a2/19295f6f43.shtml" target="_blank" rel="noreferrer">https://news.southcn.com/node_54a44f01a2/19295f6f43.shtml</a></p><h3 id="\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u73B0\u573A\u4F1A\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C" tabindex="-1">\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u73B0\u573A\u4F1A\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C <a class="header-anchor" href="#\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u73B0\u573A\u4F1A\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C" aria-hidden="true">#</a></h3><p>2019-09-11 06:27 \u6765\u6E90\uFF1A\u5357\u65B9\u65E5\u62A5 \u674E\u79C0\u5A77</p><p>9\u670810\u65E5\uFF0C\u56FD\u5BB6\u536B\u5065\u59D4\u5728\u5E7F\u5DDE\u82B1\u90FD\u53EC\u5F00\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u65B0\u95FB\u53D1\u5E03\u4F1A\uFF0C\u63A8\u4ECB\u5E7F\u4E1C\u57FA\u5C42\u533B\u6539\u7ECF\u9A8C\u3002\u8FD9\u5DF2\u7ECF\u662F\u8FDE\u7EED\u4E09\u5E74\u56FD\u5BB6\u5728\u5E7F\u4E1C\u53EC\u5F00\u533B\u6539\u5DE5\u4F5C\u65B0\u95FB\u53D1\u5E03\u4F1A\u3002</p><p>\u4EE5\u5E7F\u5DDE\u82B1\u90FD\u4E3A\u4EE3\u8868\uFF0C\u5E7F\u4E1C\u521B\u9020\u6027\u5730\u5728\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\u5B9E\u65BD\u201C\u516C\u76CA\u4E00\u7C7B\u8D22\u653F\u4F9B\u7ED9\u3001\u516C\u76CA\u4E8C\u7C7B\u7EE9\u6548\u7BA1\u7406\u201D\u7684\u4E3E\u63AA\uFF0C\u53D6\u5F97\u4E86\u7559\u4F4F\u57FA\u5C42\u4EBA\u624D\u3001\u589E\u5F3A\u4E1A\u52A1\u80FD\u529B\u3001\u63D0\u5347\u533B\u7597\u8D28\u91CF\u3001\u8D62\u5F97\u7FA4\u4F17\u6EE1\u610F\u7684\u6210\u6548\uFF0C\u6253\u9020\u51FA\u4E86\u5168\u56FD\u57FA\u5C42\u536B\u751F\u7EFC\u5408\u6539\u9769\u7684\u201C\u5E7F\u4E1C\u533B\u6539\u7ECF\u9A8C\u201D\u3002</p><p><strong>\u6751\u7A33</strong></p><p>**\u3000\u3000\u6751\u6C11\u201C\u5C0F\u75C5\u4E0D\u51FA\u6751\u201D**</p><p>\u4ECE\u7B80\u964B\u7684\u7816\u5934\u623F\u5230\u201C\u516D\u5BA4\u4E00\u5385\u201D\uFF0C\u914D\u9F5019\u79CD\u57FA\u672C\u533B\u7597\u8BBE\u5907\uFF0C\u4ECE\u571F\u751F\u571F\u957F\u7684\u201C\u8D64\u811A\u5927\u592B\u201D\u5230\u7EDF\u4E00\u62DB\u8058\u7684\u533B\u5B66\u4E13\u4E1A\u6BD5\u4E1A\u751F\uFF0C\u8FD1\u5341\u5E74\u6765\uFF0C\u82B1\u90FD\u533A\u4E61\u6751\u536B\u751F\u7AD9\u5B8C\u6210\u4E86\u4E00\u573A\u8776\u53D8\u3002</p><p>\u6539\u53D8\u4ECE2009\u5E74\u5F00\u59CB\u3002\u8FD9\u4E00\u5E74\uFF0C\u82B1\u90FD\u5C06\u4E61\u6751\u536B\u751F\u7AD9\u6536\u5F52\u9547\u536B\u751F\u9662\u7EDF\u4E00\u7BA1\u7406\uFF0C\u6539\u4E3A\u516C\u76CA\u6027\u8D28\u3002\u82B1\u90FD\u6295\u51651200\u4E07\u5143\uFF0C\u5BF9196\u4E2A\u6751\u536B\u751F\u7AD9\u5B9E\u884C\u6807\u51C6\u5316\u5EFA\u8BBE\u3002\u5982\u4ECA\uFF0C\u82B1\u90FD\u533A\u6BCF\u95F4\u6751\u536B\u751F\u7AD9\u90FD\u5BBD\u655E\u660E\u4EAE\u3001\u8BBE\u65BD\u4FF1\u5168\uFF0C\u9762\u79EF\u8D85100\u5E73\u65B9\u7C73\uFF0C\u5168\u90E8\u8BBE\u7F6E\u8BCA\u5BA4\u3001\u5904\u7F6E\u5BA4\u3001\u4FDD\u5065\u5BA4\u3001\u6D88\u6BD2\u5BA4\u3001\u836F\u623F\u7B49\u529F\u80FD\u533A\uFF0C\u5E76\u914D\u5907\u57FA\u672C\u533B\u7597\u8BBE\u5907\u548C\u529E\u516C\u8BBE\u65BD\u3002</p><p>\u540C\u65F6\uFF0C\u6B63\u89C4\u533B\u5B66\u9662\u6821\u7684\u6BD5\u4E1A\u751F\u9010\u6B65\u4EE3\u66FF\u4E86\u4F20\u7EDF\u8001\u4E61\u533B\u3002\u82B1\u90FD\u533A\u5BF9\u4E61\u533B\u5B9E\u884C\u201C\u533A\u62DB\u3001\u9547\u7BA1\u3001\u6751\u7528\u201D\uFF0C\u7531\u536B\u751F\u9662\u7EDF\u4E00\u7BA1\u7406\uFF0C\u4E61\u533B\u5728\u536B\u751F\u7AD9\u5DE5\u4F5C\u6EE1\u4E24\u5E74\uFF0C\u53EF\u81EA\u7531\u9009\u62E9\u662F\u91CD\u56DE\u9547\u536B\u751F\u9662\u8FD8\u662F\u7EE7\u7EED\u7559\u5728\u6751\u536B\u751F\u7AD9\u3002</p><p>2018\u5E74\uFF0C\u82B1\u90FD\u5C06\u4E61\u533B\u7F16\u5236\u5168\u90E8\u5E76\u5165\u4E86\u536B\u751F\u9662\u7F16\u5236\u7BA1\u7406\uFF0C\u4E61\u533B\u5168\u90E8\u53D8\u6210\u4E86\u201C\u9547\u533B\u201D\uFF0C\u62E5\u6709\u7EDF\u4E00\u7684\u53D1\u5C55\u5E73\u53F0\u548C\u804C\u79F0\u664B\u5347\u6E20\u9053\u3002\u8FD9\u8BA9\u4E61\u533B\u4EEC\u66F4\u5B89\u5FC3\u5730\u624E\u6839\u5728\u536B\u751F\u7AD9\u3002</p><p>\u201C90\u540E\u201D\u533B\u751F\u6881\u8F89\u6B66\u662F\u82B1\u5C71\u9547\u536B\u751F\u9662\u7684\u4E00\u540D\u5728\u7F16\u4E61\u533B\uFF0C\u4E0D\u4E45\u524D\u201C\u4E0B\u6751\u201D\u6765\u5230\u82B1\u5C71\u9547\u6C38\u4E50\u6751\u536B\u751F\u7AD9\u5DE5\u4F5C\u3002\u4E4B\u524D\uFF0C\u4ED6\u5728\u4F5B\u5C71\u5E02\u4E00\u5BB6\u4E09\u7532\u533B\u9662\u505A\u4E863\u5E74\u5916\u79D1\u533B\u751F\u3002\u4ECE\u57CE\u5E02\u91CC\u7684\u4E09\u7532\u533B\u9662\u5230\u6751\u91CC\u7684\u536B\u751F\u7AD9\uFF0C\u6881\u8F89\u6B66\u7684\u201C\u9006\u5411\u9009\u62E9\u201D\uFF0C\u9664\u4E86\u5BB6\u5EAD\u539F\u56E0\u5916\uFF0C\u6B63\u662F\u7531\u4E8E\u770B\u4E2D\u4E86\u8FD9\u91CC\u7684\u6B63\u89C4\u7F16\u5236\u548C\u664B\u5347\u6E20\u9053\u3002</p><p>\u8D8A\u6765\u8D8A\u591A\u201C\u9AD8\u5B66\u5386\u3001\u9AD8\u6280\u672F\u3001\u9AD8\u7D20\u517B\u201D\u7684\u533B\u751F\u6765\u5230\u6751\u6C11\u5BB6\u95E8\u53E3\uFF0C\u533B\u7597\u8D28\u91CF\u6709\u4FDD\u969C\uFF0C\u6751\u6C11\u4EEC\u5728\u536B\u751F\u7AD9\u5C31\u8BCA\u7684\u610F\u613F\u8D8A\u6765\u8D8A\u9AD8\u3002</p><p>\u5728\u6751\u536B\u751F\u7AD9\u201C\u5347\u7EA7\u6362\u4EE3\u201D\u7684\u540C\u65F6\uFF0C\u82B1\u90FD\u533A\u201C\u4E00\u5143\u94B1\u770B\u75C5\u201D\u7684\u60E0\u6C11\u4E3E\u63AA\u4E5F\u6539\u53D8\u4E86\u6751\u6C11\u4EEC\u7684\u5C31\u533B\u4E60\u60EF\u30022010\u5E749\u6708\u8D77\uFF0C\u82B1\u90FD\u533A\u8D22\u653F\u6301\u7EED\u6295\u5165\uFF0C\u5728\u5168\u533A\u6751\u536B\u751F\u7AD9\u63A8\u5F00\u201C\u4E00\u5143\u94B1\u770B\u75C5\u201D\uFF0C\u6751\u6C11\u5728\u6751\u536B\u751F\u7AD9\u770B\u75C5\uFF0C\u53EA\u9700\u7F34\u4ED81\u5143\u6302\u53F7\u8D39\uFF0C\u82E5\u9700\u6CE8\u5C04\uFF0C\u53E6\u4EA4\u6CE8\u5C04\u8D391\u5143\uFF0C\u836F\u54C1\u53CA\u68C0\u67E5\u5747\u514D\u8D39\u3002</p><p>\u4ECA\u5E741\u20145\u6708\uFF0C\u4EC5\u82B1\u90FD\u533A\u82B1\u5C71\u9547\u4E00\u4E2A\u9547\uFF0C\u5C31\u4E3A\u7FA4\u4F17\u8282\u7701\u4E86\u603B\u8BA1139\u4E07\u4F59\u5143\u533B\u7597\u8D39\u7528\u3002\u201C\u5C0F\u75C5\u4E0D\u51FA\u6751\uFF0C\u4E00\u5143\u94B1\u641E\u6382\u201D\uFF0C\u6E10\u6E10\u5730\uFF0C\u6751\u6C11\u4EEC\u5C0F\u75C5\u4E0D\u518D\u62D6\uFF0C\u6162\u6027\u75C5\u4E5F\u5F97\u5230\u4E86\u826F\u597D\u63A7\u5236\u7BA1\u7406\uFF0C\u964D\u4F4E\u4E86\u5927\u75C5\u7684\u53D1\u751F\u6982\u7387\u3002</p><p>\u56FD\u5BB6\u536B\u751F\u5065\u5EB7\u59D4\u4F53\u6539\u53F8\u526F\u53F8\u957F\u5E84\u5B81\u8BC4\u4EF7\uFF0C\u5E7F\u5DDE\u5E02\u82B1\u90FD\u533A\u63A2\u7D22\u51FA\u4E86\u4E00\u6761\u53EF\u501F\u9274\u3001\u53EF\u63A8\u5E7F\u7684\u201C\u6751\u7A33\u201D\u6539\u9769\u8DEF\u5F84\u3002</p><p>**\u3000\u3000\u9547\u6D3B**</p><p>**\u3000\u3000\u533B\u751F\u5E74\u5747\u6536\u516525\u4E07\u5143**</p><p>\u82B1\u90FD\u533A\u57FA\u5C42\u533B\u7597\u5B9E\u73B0\u201C\u6751\u7A33\u201D\u7684\u80CC\u540E\uFF0C\u662F\u4E00\u4E2A\u4E2A\u5145\u6EE1\u6D3B\u529B\u7684\u9547\u7EA7\u536B\u751F\u9662\u3002\u4EE5\u82B1\u5C71\u9547\u536B\u751F\u9662\u4E3A\u4F8B\uFF0C\u4ECA\u5E741\u20146\u6708\u95E8\u8BCA\u91CF\u540C\u6BD4\u589E\u957F11.52%\uFF0C\u4F4F\u9662\u4EBA\u6B21\u540C\u6BD4\u589E\u957F48.56%\uFF0C\u4E1A\u52A1\u603B\u6536\u5165\u540C\u6BD4\u589E\u957F36.36%\u3002</p><p>\u5305\u62EC\u4E61\u533B\u5728\u5185\uFF0C\u82B1\u90FD\u533A\u7684\u9547\u536B\u751F\u9662\u533B\u52A1\u4EBA\u5458\u6536\u5165\u5B9E\u73B0\u4E86\u663E\u8457\u589E\u957F\u30022010\u5E74\uFF0C\u82B1\u5C71\u9547\u536B\u751F\u9662\u804C\u5DE5\u5E73\u5747\u6536\u5165\u4EC56.78\u4E07\u5143\uFF0C2015\u5E74\u4E3A10.97\u4E07\u5143\uFF0C2017\u5E74\u4FBF\u589E\u957F\u5230\u4E8623.11\u4E07\u5143\uFF0C2018\u5E74\u8FBE\u523025.41\u4E07\u5143\u3002</p><p>\u4E3A\u4F55\u4E00\u5BB6\u9547\u536B\u751F\u9662\u7684\u57FA\u5C42\u533B\u751F\u6536\u5165\u80FD\u5728\u77ED\u77ED\u51E0\u5E74\u5185\u5B9E\u73B0\u201C\u51E0\u7EA7\u8DF3\u201D\uFF1F\u5965\u79D8\u5728\u4E8E\u5E7F\u4E1C\u5B9E\u884C\u201C\u4E00\u7C7B\u4F9B\u7ED9\u4E8C\u7C7B\u7BA1\u7406\u201D\u7684\u5927\u80C6\u6539\u9769\u3002</p><p>2011\u5E74\uFF0C\u5E7F\u4E1C\u5C06\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\uFF08\u9547\u536B\u751F\u9662\u53CA\u793E\u536B\u4E2D\u5FC3\uFF09\u5217\u4E3A\u516C\u76CA\u4E00\u7C7B\u4E8B\u4E1A\u5355\u4F4D\uFF0C\u653F\u5E9C\u5168\u989D\u62E8\u6B3E\uFF0C\u5B9E\u65BD\u201C\u6536\u652F\u4E24\u6761\u7EBF\u201D\u7BA1\u7406\u3002\u8FD9\u4E00\u653F\u7B56\u8BA9\u4E00\u4E9B\u504F\u8FDC\u3001\u8584\u5F31\u3001\u6548\u76CA\u4E0D\u597D\u7684\u9547\u536B\u751F\u9662\u53D7\u5230\u4E86\u201C\u515C\u5E95\u201D\u4FDD\u969C\uFF0C\u4F46\u5728\u5B9E\u9645\u8FD0\u884C\u4E2D\uFF0C\u5176\u5F0A\u7AEF\u4E5F\u9010\u6E10\u663E\u73B0\u3002</p><p>\u5728\u201C\u5927\u9505\u996D\u201D\u4E0B\uFF0C\u5E72\u591A\u5E72\u5C11\u90FD\u4E00\u6837\uFF0C\u57FA\u5C42\u533B\u751F\u4E0D\u613F\u610F\u770B\u75C5\u3001\u63A8\u8BFF\u75C5\u4EBA\u73B0\u8C61\u65F6\u6709\u53D1\u751F\u3002\u5E7F\u5DDE\u5E02\u82B1\u90FD\u533A\u67D0\u536B\u751F\u9662\uFF0C\u6BCF\u4E2A\u6708\u533B\u751F\u7EE9\u6548\u5956\u52B1\u76F8\u5DEE\u53EA\u6709\u4E00\u4E24\u767E\u5143\uFF0C\u4E3A\u4E86\u5C3D\u91CF\u5C11\u5E72\u6D3B\uFF0C\u6709\u7684\u533B\u751F\u53EA\u8981\u75C5\u4EBA\u75C5\u60C5\u7A0D\u5FAE\u590D\u6742\uFF0C\u5C31\u5EFA\u8BAE\u8F6C\u8BCA\u53BB\u4E0A\u7EA7\u533B\u9662\u3002</p><p>\u57FA\u5C42\u85AA\u916C\u5F85\u9047\u4F4E\uFF0C\u5438\u5F15\u4E0D\u4E86\u4EBA\u624D\uFF0C\u4E5F\u7559\u4E0D\u4F4F\u4EBA\u624D\u3002\u636E\u8C03\u7814\u663E\u793A\uFF0C\u5E7F\u4E1C\u591A\u6570\u5730\u533A\u57FA\u5C42\u533B\u751F\u6536\u5165\u4EC5\u76F8\u5F53\u4E8E\u540C\u804C\u79F0\u6559\u5E08\u76843/4\u6216\u66F4\u4F4E\uFF0C\u4E0E\u672C\u5730\u53BF\u7EA7\u516C\u7ACB\u533B\u9662\u76F8\u6BD4\u4F4E3\u4E07\u20144\u4E07\u5143\u30022015\u5E74\uFF0C\u7CA4\u4E1C\u897F\u5317\u4E61\u9547\u536B\u751F\u9662\u8FD1\u5E74\u65B0\u62DB\u8058\u533B\u52A1\u4EBA\u5458\u6D41\u5931\u7387\u8FBE39%\uFF0C\u6D41\u5931\u4EBA\u5458\u4E2D\u672C\u79D1\u4EE5\u4E0A\u5B66\u5386\u536083%\u3002</p><p>\u4EBA\u624D\u6D41\u5931\uFF0C\u4F7F\u5F97\u57FA\u5C42\u533B\u7597\u670D\u52A1\u80FD\u529B\u660E\u663E\u6ED1\u5761\u3002\u5982\u4F55\u6253\u7834\u8FD9\u4E00\u50F5\u5C40\u3001\u6FC0\u53D1\u57FA\u5C42\u6D3B\u529B\uFF1F2016\u5E74\u4EE5\u6765\uFF0C\u7701\u59D4\u3001\u7701\u653F\u5E9C\u4E3B\u8981\u8D1F\u8D23\u4EBA\u5206\u522B\u5E26\u961F\uFF0C\u5148\u540E\u6DF1\u5165\u97F6\u5173\u3001\u6885\u5DDE\u3001\u60E0\u5DDE\u3001\u4E1C\u839E\u3001\u4E2D\u5C71\u3001\u6C5F\u95E8\u3001\u6E05\u8FDC\u7B49\u4E00\u7EBF\u8C03\u7814\uFF0C\u5E76\u4E13\u95E8\u542C\u53D6\u4E00\u7EBF\u533B\u52A1\u4EBA\u5458\u7684\u610F\u89C1\u5EFA\u8BAE\u3002</p><p>\u5EA7\u8C08\u8C03\u7814\u4E2D\uFF0C\u57FA\u5C42\u666E\u904D\u53CD\u6620\uFF0C\u4F5C\u4E3A\u516C\u76CA\u4E00\u7C7B\u4E8B\u4E1A\u5355\u4F4D\uFF0C\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\u7EE9\u6548\u5DE5\u8D44\u5360\u6BD4\u8F83\u5C0F\uFF0C\u7EE9\u6548\u5DE5\u8D44\u603B\u91CF\u9650\u5236\u8F83\u4E25\uFF0C\u4E0D\u5F97\u7A81\u7834\uFF1B\u5728\u7F16\u5236\u4E0A\u9650\u5236\u8F83\u591A\uFF0C\u7075\u6D3B\u6027\u4E0D\u591F\u3002\u8FD9\u4E9B\u90FD\u9650\u5236\u4E86\u536B\u751F\u9662\u53D1\u5C55\u7684\u201C\u62F3\u811A\u201D\u3002</p><p>2017\u5E741\u6708\uFF0C\u5E7F\u4E1C\u5728\u5168\u56FD\u7387\u5148\u542F\u52A8\u57FA\u5C42\u533B\u7597\u7EFC\u5408\u6539\u9769\uFF0C\u660E\u786E\u5141\u8BB8\u4E61\u9547\u536B\u751F\u9662\u548C\u793E\u536B\u4E2D\u5FC3\u5B9E\u884C\u201C\u516C\u76CA\u4E00\u7C7B\u8D22\u653F\u4F9B\u7ED9\uFF0C\u516C\u76CA\u4E8C\u7C7B\u4E8B\u4E1A\u5355\u4F4D\u7BA1\u7406\u201D\uFF0C\u4EBA\u5458\u5B9E\u884C\u53BF\u62DB\u53BF\u7BA1\u9547\u7528\uFF0C\u7EE9\u6548\u5DE5\u8D44\u603B\u91CF\u4E0D\u4E88\u9650\u5236\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u4E0B\u653E\u6743\u529B\uFF0C\u8D4B\u4E88\u9662\u957F\uFF08\u4E3B\u4EFB\uFF09\u201C\u7528\u4EBA\u6743\u201D\u201C\u505A\u4E8B\u6743\u201D\u201C\u5206\u914D\u6743\u201D\u3002</p><p>\u8FD9\u4E00\u7CFB\u5217\u6539\u9769\u63AA\u65BD\u7684\u201C\u677E\u7ED1\u201D\u6548\u679C\uFF0C\u5728\u5E7F\u4E1C\u5404\u5730\u9010\u6E10\u663E\u73B0\u3002</p><p>2018\u5E74\u8D77\uFF0C\u82B1\u90FD\u533A\u660E\u786E\u63D0\u51FA\uFF0C\u57FA\u5C42\u533B\u7597\u673A\u6784\u4E0D\u518D\u6267\u884C\u201C\u6536\u652F\u4E24\u6761\u7EBF\u201D\uFF0C\u5B9E\u73B0\u201C\u4E00\u7C7B\u4F9B\u7ED9\u4E8C\u7C7B\u7BA1\u7406\u201D\uFF0C\u536B\u751F\u9662\u6536\u5165\u7ED3\u4F59\u768460%\u5411\u4E34\u5E8A\u4E00\u7EBF\u548C\u5DE5\u4F5C\u4E1A\u7EE9\u7A81\u51FA\u7684\u804C\u5DE5\u503E\u659C\u3002</p><p>\u201C\u5927\u9505\u996D\u201D\u88AB\u6253\u7834\uFF0C\u57FA\u5C42\u533B\u52A1\u4EBA\u5458\u7684\u79EF\u6781\u6027\u88AB\u5145\u5206\u6FC0\u53D1\u30022018\u5E74\uFF0C\u82B1\u90FD\u533A\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\u95E8\u6025\u8BCA\u91CF\u540C\u6BD4\u589E\u957F18.0%\uFF0C\u4F4F\u9662\u91CF\u540C\u6BD4\u589E\u957F13.7%\u30022018\u5E74\uFF0C\u5168\u533A\u57FA\u5C42\u4E1A\u52A1\u91CF\u6536\u652F\u7ED3\u4F592226\u4E07\u5143\uFF0C\u5176\u4E2D60%\u7528\u4E8E\u589E\u53D1\u533B\u52A1\u4EBA\u5458\u7EE9\u6548\u3002</p><p>\u4EE5\u82B1\u5C71\u9547\u4E3A\u4F8B\uFF0C\u8BE5\u9662\u804C\u5DE5\u53BB\u5E74\u5E73\u5747\u6536\u5165\u8FBE\u523025.41\u4E07\u5143\u30022019\u5E741\u81F36\u6708\uFF0C\u82B1\u5C71\u9547\u536B\u751F\u9662\u5E74\u95E8\u8BCA\u91CF\u540C\u6BD4\u589E\u957F11.52%\uFF0C\u4F4F\u9662\u4EBA\u6B21\u540C\u6BD4\u589E\u957F48.56%\uFF0C\u4E1A\u52A1\u603B\u6536\u5165\u540C\u6BD4\u589E\u957F36.36%\uFF0C\u836F\u5360\u6BD4\u540C\u6BD4\u4E0B\u964D\u4E8610.32%\u3002</p><p>**\u3000\u3000\u6293\u4F4F\u57FA\u5C42\u533B\u6539\u7684\u201C\u725B\u9F3B\u5B50\u201D**</p><p>\u8FD1\u5E74\u6765\uFF0C\u4EE5\u201C\u4E00\u7C7B\u4F9B\u7ED9\u4E8C\u7C7B\u7BA1\u7406\u201D\u4E3A\u6838\u5FC3\u7684\u5E7F\u4E1C\u57FA\u5C42\u536B\u751F\u7EFC\u5408\u6539\u9769\u7ECF\u9A8C\u4E0D\u65AD\u8D70\u5411\u5168\u56FD\u3002\u9664\u201C\u82B1\u90FD\u7ECF\u9A8C\u201D\u5916\uFF0C\u5E7F\u5DDE\u5E02\u589E\u57CE\u533A\uFF0C\u7ECF\u6D4E\u6B20\u53D1\u8FBE\u5730\u533A\u97F6\u5173\u5E02\u3001\u6E05\u8FDC\u82F1\u5FB7\u5E02\u7684\u6539\u9769\u6210\u6548\u90FD\u4EE4\u4EBA\u77A9\u76EE\u3002</p><p>\u81EA2011\u5E74\u8D77\uFF0C\u6E05\u8FDC\u82F1\u5FB7\u5E02\u5C31\u5F00\u59CB\u63A2\u7D22\u57FA\u5C42\u533B\u7597\u673A\u6784\u201C\u4E00\u7C7B\u4F9B\u7ED9\u4E8C\u7C7B\u7BA1\u7406\u201D\u7684\u8FD0\u884C\u6A21\u5F0F\u3002\u5F7C\u65F6\u7684\u82F1\u5FB7\u5E02\uFF0C\u653F\u5E9C\u5BF9\u57FA\u5C42\u533B\u7597\u6295\u5165\u4E0D\u8DB3\uFF0C\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\u8BBE\u5907\u9648\u65E7\u3001\u533B\u751F\u6D41\u5931\u4E25\u91CD\uFF0C\u90E8\u5206\u5904\u4E8E\u4E3E\u6B65\u7EF4\u8270\u7684\u751F\u5B58\u56F0\u5883\u3002\u52A0\u5927\u57FA\u5C42\u536B\u751F\u9662\u6295\u5165\u3001\u63D0\u9AD8\u533B\u52A1\u4EBA\u5458\u5DE5\u8D44\u5F85\u9047\u6210\u4E3A\u201C\u89E3\u9898\u201D\u7684\u5173\u952E\u3002</p><p>\u4E00\u65B9\u9762\uFF0C\u82F1\u5FB7\u52A0\u5927\u57FA\u5C42\u536B\u751F\u7ECF\u8D39\u6295\u5165\uFF0C\u4ECE\u6539\u9769\u524D\u7684\u6BCF\u5E74160\u591A\u4E07\u5143\u589E\u52A0\u52302017\u5E74\u76841.6\u4EBF\u5143\uFF0C\u5BF9\u57FA\u5C42\u533B\u7597\u673A\u6784\u8D22\u653F\u8DB3\u989D\u8865\u52A9\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u5141\u8BB8\u57FA\u5C42\u533B\u7597\u673A\u6784\u7A81\u7834\u7EE9\u6548\u5DE5\u8D44\u603B\u91CF\uFF0C\u5141\u8BB8\u533B\u7597\u6536\u5165\u6263\u9664\u6210\u672C\u540E\u6309\u89C4\u5B9A\u63D0\u53D6\u4E3A\u5956\u52B1\u6027\u7EE9\u6548\u5DE5\u8D44\uFF0C\u57FA\u5C42\u53EF\u81EA\u4E3B\u5236\u5B9A\u5956\u52B1\u6027\u7EE9\u6548\u5DE5\u8D44\u5206\u914D\u65B9\u6848\u3002</p><p>\u6539\u9769\u6210\u6548\u5F88\u5FEB\u663E\u73B0\u30022018\u5E74\uFF0C\u82F1\u5FB7\u5E02\u57FA\u5C42\u95E8\u6025\u8BCA\u4EBA\u6B21\u5360\u5168\u5E02\u603B\u91CF\u768467%\uFF0C\u57FA\u5C42\u95E8\u6025\u8BCA\u4EBA\u6570\u548C\u4F4F\u9662\u4EBA\u6570\u5206\u522B\u6BD4\u6539\u9769\u524D\u589E\u957F1.7\u500D\u548C93.4%\uFF0C\u75C5\u4EBA\u56DE\u6D41\u57FA\u5C42\u660E\u663E\u3002\u5F53\u5E74\uFF0C\u5168\u5E02\u4E61\u9547\u536B\u751F\u9662\u533B\u52A1\u4EBA\u5458\u5E74\u5747\u6536\u5165\u8FBE10.7\u4E07\u5143\uFF0C\u6BD42016\u5E74\u76848.7\u4E07\u5143\u589E\u957F\u4E8623%\uFF0C\u4E0E\u5E02\u76F4\u5C5E\u533B\u9662\u57FA\u672C\u6301\u5E73\u3002</p><p>\u4F4D\u4E8E\u7CA4\u897F\u5730\u533A\u7684\u9633\u6C5F\u5E02\u9633\u897F\u53BF\uFF0C\u63A2\u7D22\u4E86\u4E00\u6761\u5EFA\u8BBE\u53BF\u57DF\u533B\u5171\u4F53\u6765\u63D0\u5347\u57FA\u5C42\u533B\u7597\u670D\u52A1\u80FD\u529B\u7684\u8DEF\u5B50\u3002\u5176\u4E2D\u201C\u4E00\u7C7B\u4F9B\u7ED9\u4E8C\u7C7B\u7BA1\u7406\u201D\u4E5F\u662F\u6FC0\u6D3B\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\u6D3B\u529B\u7684\u5173\u952E\u4E00\u62DB\u3002</p><p>2018\u5E741\u6708\u8D77\uFF0C\u9633\u897F\u5C31\u660E\u786E\u4E86\u5404\u9547\u536B\u751F\u9662\u4E0D\u518D\u5B9E\u65BD\u201C\u6536\u652F\u4E24\u6761\u7EBF\u201D\uFF0C\u5168\u9762\u5B9E\u73B0\u201C\u4E00\u7C7B\u4F9B\u7ED9\u4E8C\u7C7B\u7BA1\u7406\u201D\uFF0C\u6700\u5927\u9650\u5EA6\u91CA\u653E\u4E86\u57FA\u5C42\u6D3B\u529B\u30022018\u5E74\uFF0C\u9633\u897F\u53BF\u9547\u536B\u751F\u9662\u95E8\u6025\u8BCA\u91CF\u589E\u957F18%\uFF0C\u4F4F\u9662\u91CF\u589E\u957F21%\uFF0C\u533B\u7597\u670D\u52A1\u5360\u6BD4\u5347\u523032%\uFF0C\u4EBA\u5458\u7ECF\u8D39\u652F\u51FA\u5360\u6BD4\u5347\u523045.3%\u3002</p><p>\u201C\u5E7F\u4E1C\u7684\u57FA\u5C42\u533B\u6539\u5BF9\u5168\u56FD\u6DF1\u5316\u57FA\u5C42\u7EFC\u5408\u6539\u9769\u5177\u6709\u91CD\u8981\u7684\u501F\u9274\u610F\u4E49\u3002\u201D\u5E84\u5B81\u8868\u793A\uFF0C\u5E7F\u4E1C\u5728\u57FA\u5C42\u843D\u5B9E\u201C\u4E24\u4E2A\u5141\u8BB8\u201D\uFF0C\u63A8\u8FDB\u201C\u4E00\u7C7B\u4FDD\u969C\u4E8C\u7C7B\u7BA1\u7406\u201D\uFF0C\u6709\u6548\u89E3\u51B3\u57FA\u5C42\u52A8\u529B\u4E0D\u8DB3\u3001\u6D3B\u529B\u4E0D\u591F\u7B49\u95EE\u9898\u3002</p><p>\u5E7F\u4E1C\u7701\u536B\u5065\u59D4\u515A\u7EC4\u4E66\u8BB0\u3001\u4E3B\u4EFB\u6BB5\u5B87\u98DE\u4ECB\u7ECD\uFF0C\u6539\u9769\u540E\uFF0C\u6211\u7701\u57FA\u5C42\u673A\u6784\u4EBA\u624D\u961F\u4F0D\u4E0D\u65AD\u58EE\u5927\uFF0C\u670D\u52A1\u80FD\u529B\u660E\u663E\u63D0\u5347\u3002\u4E0E2016\u5E74\u76F8\u6BD4\uFF0C2018\u5E74\u5168\u7701\u57FA\u5C42\u673A\u6784\u9AD8\u7EA7\u804C\u79F0\u536B\u751F\u6280\u672F\u4EBA\u5458\u589E\u52A027.2%\uFF0C\u672C\u79D1\u4EE5\u4E0A\u5B66\u5386\u533B\u751F\u603B\u91CF\u589E\u52A039.8%\u3002</p><p>2019\u5E74\u4E0A\u534A\u5E74\uFF0C\u5168\u7701\u4E61\u9547\u536B\u751F\u9662\u603B\u8BCA\u7597\u4EBA\u6B213839.4\u4E07\u3001\u51FA\u9662\u4EBA\u6B2195.2\u4E07\uFF0C\u5206\u522B\u8F83\u53BB\u5E74\u540C\u671F\u589E\u957F7.7%\u30015.1%\uFF0C\u57FA\u5C42\u533B\u7597\u536B\u751F\u673A\u6784\u5BF9\u4E8E\u57FA\u5C42\u7FA4\u4F17\u7684\u5438\u5F15\u529B\u5728\u9010\u6B65\u589E\u5F3A\u3002</p><p>\u5E7F\u4E1C\u7701\u201C\u4E00\u7C7B\u4FDD\u969C\u4E8C\u7C7B\u7BA1\u7406\u201D\u7684\u521B\u65B0\u6539\u9769\u4E3E\u63AA\u5F97\u5230\u57FA\u5C42\u7684\u5E7F\u6CDB\u597D\u8BC4\u548C\u884C\u4E1A\u5185\u90E8\u7684\u9AD8\u5EA6\u8BA4\u540C\u3002\u622A\u81F32018\u5E7412\u6708\uFF0C\u5E7F\u4E1C\u7701\u5DF2\u670920\u4E2A\u5730\u7EA7\u5E02\u300182\u4E2A\u53BF\uFF08\u5E02\u3001\u533A\uFF09\u51FA\u53F0\u6587\u4EF6\uFF0C\u8D2F\u5F7B\u843D\u5B9E\u3002\u7EE7\u5E7F\u4E1C\u540E\uFF0C\u6D77\u5357\u7701\u3001\u6E56\u5357\u7701\u3001\u5C71\u897F\u7701\u592A\u539F\u5E02\u5747\u5370\u53D1\u6587\u4EF6\uFF0C\u5168\u9762\u5B9E\u65BD\u8FD9\u4E00\u4E3E\u63AA\u3002</p><p>\uFF08\u5357\u65B9\u65E5\u62A5\u8BB0\u8005 \u674E\u79C0\u5A77\u3000\u901A\u8BAF\u5458 \u7CA4\u536B\u4FE1\uFF09</p>',45),s=[n];function o(l,_,i,c,d,h){return t(),e("div",null,s)}const u=p(r,[["render",o]]);export{m as __pageData,u as default};
