import{_ as p,c as t,o as e,e as r}from"./app.be968c44.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"title: \u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8\u6216\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168 \u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8","slug":"title-\u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8\u6216\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168-\u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8","link":"#title-\u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8\u6216\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168-\u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8","children":[]}],"relativePath":"1-100\u671F/_doc/8-1.md","lastUpdated":1666361649000}'),o={name:"1-100\u671F/_doc/8-1.md"},n=r('<hr><h2 id="title-\u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8\u6216\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168-\u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8" tabindex="-1">title: \u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8\u6216\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168 \u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8 <a class="header-anchor" href="#title-\u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8\u6216\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168-\u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8" aria-hidden="true">#</a></h2><p><a href="http://www.xinhuanet.com/politics/2019-08/03/c_1124832007.htm" target="_blank" rel="noreferrer">http://www.xinhuanet.com/politics/2019-08/03/c_1124832007.htm</a></p><p><strong>\u56FD\u52A1\u9662\u63D0\u51FA\u529B\u4E89\u9010\u5E74\u964D\u4F4E\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u7387</strong></p><p><strong>\u63D0\u9AD8\u56FD\u6C11\u7D20\u8D28\u8FDC\u79BB\u8FD1\u89C6</strong></p><p>\u25CF\u3000\u6211\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u53D1\u75C5\u5F62\u52BF\u4E25\u5CFB\uFF0C2018\u5E74\u5168\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u7387\u4E3A53.6%\u3002\u5176\u4E2D\uFF0C6\u5C81\u513F\u7AE5\u4E3A14.5%\uFF0C\u5C0F\u5B66\u751F\u4E3A36%\uFF0C\u521D\u4E2D\u751F\u4E3A71.6%\uFF0C\u9AD8\u4E2D\u751F\u4E3A81%\uFF0C\u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8</p><p>\u25CF\u3000\u7EB5\u89C2\u8FD9\u51E0\u5E74\u56FD\u5BB6\u653F\u7B56\u7684\u8D70\u5411\u53EF\u4EE5\u53D1\u73B0\uFF0C\u653F\u7B56\u89C4\u5B9A\u8D8A\u6765\u8D8A\u660E\u786E\u3001\u5177\u4F53\uFF0C\u5177\u6709\u53EF\u64CD\u4F5C\u6027\u3002\u4E0B\u4E00\u6B65\u7684\u5173\u952E\u662F\u5728\u5730\u65B9\u653F\u5E9C\u548C\u5B66\u6821\u4E2D\u843D\u5B9E\u8D23\u4EFB\u4EBA\uFF0C\u628A\u513F\u7AE5\u9752\u5C11\u5E74\u7684\u89C6\u529B\u5065\u5EB7\u60C5\u51B5\u7EB3\u5165\u5BF9\u4E2A\u4EBA\u548C\u5355\u4F4D\u7684\u8003\u6838\u6307\u6807\u91CC\uFF0C\u660E\u786E\u89C4\u5B9A\u76F8\u5E94\u7684\u5956\u60E9\u63AA\u65BD\uFF0C\u5BF9\u8FBE\u4E0D\u5230\u8981\u6C42\u7684\u4E2A\u4EBA\u548C\u5355\u4F4D\u8FDB\u884C\u95EE\u8D23</p><p>\u25CF\u3000\u4ECE\u6CD5\u6CBB\u7684\u89D2\u5EA6\u770B\uFF0C\u4E9F\u987B\u5236\u5B9A\u57FA\u672C\u533B\u7597\u536B\u751F\u4E0E\u5065\u5EB7\u4FC3\u8FDB\u6CD5\u3001\u5BB6\u5EAD\u6559\u80B2\u6CD5\uFF0C\u4FEE\u8BA2\u300A\u5B66\u6821\u536B\u751F\u5DE5\u4F5C\u6761\u4F8B\u300B\u7B49\u6CD5\u5F8B\u6CD5\u89C4\uFF0C\u4F9D\u6CD5\u660E\u786E\u76F8\u5173\u5404\u65B9\u7684\u6CD5\u5F8B\u8D23\u4EFB\uFF0C\u4FDD\u969C\u513F\u7AE5\u9752\u5C11\u5E74\u8FDC\u79BB\u8FD1\u89C6\u76EE\u6807\u7684\u5B9E\u73B0</p><p>\u636E\u56FD\u5BB6\u4F53\u80B2\u603B\u5C402010\u5E74\u53D1\u5E03\u7684\u300A\u56FD\u6C11\u4F53\u8D28\u76D1\u6D4B\u516C\u62A5\u300B\u663E\u793A\uFF0C\u6211\u56FD\u5C0F\u5B66\u751F\u8FD1\u89C6\u60A3\u75C5\u7387\u4E3A31.67%\uFF0C\u521D\u4E2D\u751F\u4E3A58.07%\uFF0C\u9AD8\u4E2D\u751F\u4E3A76.02%\u30022014\u5E74\uFF0C\u6559\u80B2\u90E8\u5168\u56FD\u5B66\u751F\u4F53\u8D28\u4E0E\u5065\u5EB7\u8C03\u7814\u7ED3\u679C\u663E\u793A\uFF0C\u4E2D\u56FD\u5C0F\u5B66\u751F\u89C6\u529B\u4E0D\u826F\u68C0\u51FA\u7387\u8FBE\u523045.71%\uFF0C\u521D\u4E2D\u751F\u8FBE\u523074.36%\uFF0C\u9AD8\u4E2D\u751F\u8FBE\u523083.28%\u3002</p><p>\u56FD\u5BB6\u5BF9\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u95EE\u9898\u7684\u5173\u6CE8\uFF0C\u6B63\u5728\u63D0\u5347\u81F3\u524D\u6240\u672A\u6709\u7684\u9AD8\u5EA6\u3002</p><p>\u8FD1\u65E5\uFF0C\u300A\u56FD\u52A1\u9662\u5173\u4E8E\u5B9E\u65BD\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8\u7684\u610F\u89C1\u300B\u5370\u53D1\uFF0C\u660E\u786E15\u4E2A\u4E13\u9879\u884C\u52A8\uFF0C\u5176\u4E2D\u5305\u62EC\u5B9E\u65BD\u4E2D\u5C0F\u5B66\u5065\u5EB7\u4FC3\u8FDB\u884C\u52A8\uFF0C\u201C\u52302022\u5E74\u548C2030\u5E74\uFF0C\u5168\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u7387\u529B\u4E89\u6BCF\u5E74\u964D\u4F4E0.5\u4E2A\u767E\u5206\u70B9\u4EE5\u4E0A\uFF0C\u65B0\u53D1\u8FD1\u89C6\u7387\u660E\u663E\u4E0B\u964D\u3002\u201D</p><p>\u63A5\u53D7\u300A\u6CD5\u5236\u65E5\u62A5\u300B\u8BB0\u8005\u91C7\u8BBF\u7684\u4E13\u5BB6\u8BA4\u4E3A\uFF0C\u4ECE\u6CD5\u6CBB\u7684\u89D2\u5EA6\u770B\uFF0C\u4E9F\u987B\u5236\u5B9A\u57FA\u672C\u533B\u7597\u536B\u751F\u4E0E\u5065\u5EB7\u4FC3\u8FDB\u6CD5\u3001\u5BB6\u5EAD\u6559\u80B2\u6CD5\uFF0C\u4FEE\u8BA2\u300A\u5B66\u6821\u536B\u751F\u5DE5\u4F5C\u6761\u4F8B\u300B\u7B49\u6CD5\u5F8B\u6CD5\u89C4\uFF0C\u4F9D\u6CD5\u660E\u786E\u76F8\u5173\u5404\u65B9\u7684\u6CD5\u5F8B\u8D23\u4EFB\uFF0C\u4FDD\u969C\u513F\u7AE5\u9752\u5C11\u5E74\u8FDC\u79BB\u8FD1\u89C6\u76EE\u6807\u7684\u5B9E\u73B0\u3002</p><p><strong>\u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u9AD8</strong></p><p><strong>\u53EF\u80FD\u5F15\u53D1\u793E\u4F1A\u95EE\u9898</strong></p><p>\u201C\u62FF\u5230\u5B69\u5B50\u7684\u5B66\u671F\u8BC4\u4EF7\u624B\u518C\u65F6\uFF0C\u770B\u5230\u5979\u4E00\u53EA\u773C\u775B\u89C6\u529B4.6,\u53E6\u4E00\u53EA\u773C\u775B\u89C6\u529B4.7,\u6211\u90FD\u4E0D\u6562\u76F8\u4FE1\uFF0C\u5979\u624D\u4E00\u5E74\u7EA7\u5440\u3002\u201D\u91C7\u8BBF\u4E2D\uFF0C\u5317\u4EAC\u5E02\u6C11\u738B\u6708\u7433\u544A\u8BC9\u300A\u6CD5\u5236\u65E5\u62A5\u300B\u8BB0\u8005\uFF0C\u5979\u7684\u5973\u513F\u7476\u7476\u5728\u4E0A\u5E7C\u513F\u56ED\u65F6\u5E76\u4E0D\u8FD1\u89C6\uFF0C\u90A3\u65F6\u53CC\u773C\u89C6\u529B\u90FD\u57285.1\u4EE5\u4E0A\uFF0C\u201C\u5F53\u65F6\u5979\u7684\u540C\u5B66\u4E2D\u5DF2\u6709\u4EBA\u8FD1\u89C6\uFF0C\u6211\u8FD8\u5728\u5FC3\u91CC\u4E3A\u81EA\u5DF1\u9A84\u50B2\u5462\u201D\u3002</p><p>\u6B64\u524D\uFF0C\u738B\u6708\u7433\u5E0C\u671B\u5973\u513F\u80FD\u591F\u5065\u5EB7\u3001\u5FEB\u4E50\u5730\u6210\u957F\uFF0C\u6240\u4EE5\u7476\u7476\u5728\u5E7C\u513F\u56ED\u5C0F\u73ED\u3001\u4E2D\u73ED\u65F6\uFF0C\u9664\u4E86\u821E\u8E48\uFF0C\u4EC0\u4E48\u90FD\u6CA1\u5B66\uFF0C\u653E\u5B66\u5C31\u662F\u5728\u5916\u9762\u75AF\u8DD1\uFF0C\u5979\u7684\u540C\u5B66\u5219\u5728\u5B66\u94A2\u7434\u3001\u7F8E\u672F\u3001\u4E50\u9AD8\u7B49\u5404\u79CD\u8BFE\u7A0B\u3002\u5230\u4E86\u5927\u73ED\uFF0C\u540C\u5B66\u4EEC\u7EB7\u7EB7\u53BB\u4E0A\u5E7C\u5C0F\u8854\u63A5\u73ED\uFF0C\u7476\u7476\u4ECD\u65E7\u662F\u73A9\u3002</p><p>\u5E7C\u513F\u56ED\u6BD5\u4E1A\u540E\uFF0C\u7476\u7476\u8FDB\u5165\u5317\u4EAC\u4E00\u6240\u666E\u901A\u5C0F\u5B66\u5C31\u8BFB\uFF0C\u5404\u79CD\u4E0D\u9002\u5F88\u5FEB\u663E\u73B0\u3002</p><p>\u5148\u662F\u5B66\u4E60\u8DDF\u4E0D\u4E0A\u3002\u8001\u5E08\u5728\u73ED\u91CC\u8C03\u7814\u540E\u53D1\u73B0\uFF0C\u5168\u73ED39\u4E2A\u5B69\u5B50\u5927\u591A\u6570\u5728\u6821\u5916\u5B66\u8FC7\u82F1\u8BED\u3001\u6570\u5B66\uFF0C\u4E8E\u662F\u6388\u8BFE\u8FDB\u5EA6\u5F88\u5FEB\uFF0C\u5E76\u8BA9\u5BB6\u957F\u7ED9\u5B69\u5B50\u8865\u8BFE\u3002\u201C\u6211\u54EA\u80FD\u7ED9\u5B69\u5B50\u8865\u597D\u8BFE\u5440\uFF0C\u53EA\u80FD\u9001\u5979\u5230\u6821\u5916\u57F9\u8BAD\u673A\u6784\u5B66\u4E60\u3002\u201D\u738B\u6708\u7433\u8BF4\u3002</p><p>\u5BB6\u5EAD\u4F5C\u4E1A\u5BF9\u7476\u7476\u6765\u8BF4\u4E5F\u6210\u4E86\u95EE\u9898\u3002\u8001\u5E08\u6CA1\u6709\u7ED9\u5B69\u5B50\u4EEC\u7559\u4E66\u9762\u4F5C\u4E1A\uFF0C\u4F46\u6709\u7F51\u4E0A\u4F5C\u4E1A\uFF0C\u8BA9\u5B69\u5B50\u4EEC\u81EA\u613F\u5728\u624B\u673AApp\u4E0A\u5B8C\u6210\u3002</p><p>\u201C\u60A8\u8BF4\u6211\u80FD\u4E0D\u8BA9\u5B69\u5B50\u5B8C\u6210\u5417\uFF1F\u201D\u636E\u738B\u6708\u7433\u4ECB\u7ECD\uFF0C\u7476\u7476\u5728\u8BFE\u5802\u4E0A\u8FD8\u6CA1\u5B8C\u5168\u5B66\u4F1A\uFF0C\u5B8C\u6210\u5BB6\u5EAD\u4F5C\u4E1A\u66F4\u662F\u8D39\u52B2\u513F\uFF0C\u201C\u6BCF\u5929\u62B1\u7740\u624B\u673A\u90FD\u8981\u534A\u5C0F\u65F6\u4EE5\u4E0A\u201D\uFF0C\u5F88\u5C11\u5230\u6237\u5916\u73A9\u800D\u3001\u8FD0\u52A8\u3002</p><p>\u56DE\u60F3\u8FC7\u53BB\u8FD9\u4E00\u5B66\u5E74\uFF0C\u738B\u6708\u7433\u53CD\u601D\u8BF4\uFF1A\u201C\u5C0F\u5B69\u4E0D\u8FD1\u89C6\u624D\u602A\u3002\u201D</p><p>\u738B\u6708\u7433\u8FD8\u53D1\u73B0\uFF0C\u7476\u7476\u6240\u5728\u73ED\u7EA7\u8FD1\u89C6\u7684\u5C0F\u670B\u53CB\u4E0D\u5728\u5C11\u6570\uFF0C\u5728\u653E\u5047\u524D\u62CD\u7684\u73ED\u7EA7\u96C6\u4F53\u7167\u4E0A\uFF0C\u67094\u4E2A\u5B69\u5B50\u5DF2\u7ECF\u6234\u4E0A\u4E86\u773C\u955C\u3002\u6839\u636E\u5BB6\u957F\u4E4B\u95F4\u7684\u8BA8\u8BBA\uFF0C\u4E24\u53EA\u773C\u775B\u89C6\u529B\u4F4E\u4E8E5.0\u7684\u5B69\u5B50\u5DEE\u4E0D\u591A\u6709\u4E00\u534A\u3002</p><p>\u8FD9\u79CD\u73B0\u8C61\u5E76\u975E\u5B64\u4F8B\u3002\u56FD\u5BB6\u536B\u751F\u5065\u5EB7\u59D4\u5458\u4F1A\u75BE\u75C5\u63A7\u5236\u5C40\u526F\u5C40\u957F\u5F20\u52C7\u5728\u4ECA\u5E744\u6708\u53EC\u5F00\u7684\u4E00\u6B21\u65B0\u95FB\u53D1\u5E03\u4F1A\u4E0A\u79F0\uFF0C\u6211\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u53D1\u75C5\u5F62\u52BF\u4E25\u5CFB\u30022018\u5E74\u5168\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u7387\u4E3A53.6%\u3002\u5176\u4E2D\uFF0C6\u5C81\u513F\u7AE5\u4E3A14.5%\uFF0C\u5C0F\u5B66\u751F\u4E3A36%\uFF0C\u521D\u4E2D\u751F\u4E3A71.6%\uFF0C\u9AD8\u4E2D\u751F\u4E3A81%\uFF0C\u8FD1\u89C6\u9632\u63A7\u4EFB\u52A1\u8270\u5DE8\u3002</p><p>\u636E\u9996\u90FD\u5E08\u8303\u5927\u5B66\u7855\u58EB\u751F\u5BFC\u5E08\u5085\u6DFB\u4ECB\u7ECD\uFF0C\u867D\u7136\u5F53\u524D\u4EBA\u4EEC\u7684\u7269\u8D28\u751F\u6D3B\u6C34\u5E73\u6709\u4E86\u6781\u5927\u7684\u6539\u5584\uFF0C\u4F46\u603B\u4F53\u4E0A\u6765\u770B\uFF0C\u513F\u7AE5\u9752\u5C11\u5E74\u7684\u5065\u5EB7\u60C5\u51B5\u4E0D\u5BB9\u4E50\u89C2\u3002\u8FD1\u89C6\u95EE\u9898\u53EA\u662F\u5176\u4E2D\u8F83\u4E3A\u660E\u663E\u7684\u95EE\u9898\u4E4B\u4E00\u3002</p><p>\u5728\u5085\u6DFB\u770B\u6765\uFF0C\u513F\u7AE5\u9752\u5C11\u5E74\u7684\u89C6\u529B\u5065\u5EB7\u6B63\u5728\u9762\u4E34\u524D\u6240\u672A\u6709\u7684\u5A01\u80C1\uFF0C\u5982\u679C\u4E0D\u53CA\u65F6\u6CBB\u7406\uFF0C\u5C06\u5F15\u53D1\u4E25\u91CD\u7684\u793E\u4F1A\u95EE\u9898\u3002</p><p>\u5317\u4EAC\u5927\u5B66\u51FA\u7248\u793E\u51FA\u7248\u7684\u300A\u56FD\u6C11\u89C6\u89C9\u5065\u5EB7\u62A5\u544A\u300B\u7814\u7A76\u540C\u6837\u6307\u51FA\uFF0C\u5982\u679C\u8FD1\u89C6\u4EBA\u53E3\u6301\u7EED\u589E\u52A0\uFF0C\u5728\u822A\u7A7A\u822A\u5929\u3001\u7CBE\u5BC6\u5236\u9020\u3001\u519B\u4E8B\u7B49\u9886\u57DF\uFF0C\u7B26\u5408\u89C6\u529B\u8981\u6C42\u7684\u52B3\u52A8\u529B\u4F1A\u9762\u4E34\u5DE8\u5927\u7F3A\u53E3\uFF0C\u751A\u81F3\u4F1A\u76F4\u63A5\u5A01\u80C1\u56FD\u5BB6\u5B89\u5168\u3002</p><p>\u9996\u90FD\u533B\u79D1\u5927\u5B66\u526F\u6559\u6388\u5218\u70AB\u9E9F\u544A\u8BC9\u300A\u6CD5\u5236\u65E5\u62A5\u300B\u8BB0\u8005\uFF0C\u6839\u636E\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u4E00\u9879\u7814\u7A76\u62A5\u544A\u53EF\u77E5\uFF0C\u76EE\u524D\u6211\u56FD\u8FD1\u89C6\u60A3\u8005\u8FBE6\u4EBF\u4EBA\uFF0C\u9752\u5C11\u5E74\u8FD1\u89C6\u7387\u5C45\u4E16\u754C\u7B2C\u4E00\u5E76\u5448\u73B0\u9010\u5E74\u4E0A\u5347\u7684\u8D8B\u52BF\uFF0C\u9752\u5C11\u5E74\u773C\u5065\u5EB7\u5DF2\u7ECF\u6210\u4E3A\u6211\u56FD\u91CD\u5927\u516C\u5171\u536B\u751F\u95EE\u9898\u3002</p><p><strong>\u7535\u5B50\u8BBE\u5907\u5A01\u80C1\u89C6\u529B</strong></p><p><strong>\u5BB6\u5EAD\u7BA1\u6559\u7EA6\u675F\u4E0D\u4E25</strong></p><p>\u5176\u5B9E\uFF0C\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u95EE\u9898\u65E9\u5DF2\u5F15\u8D77\u56FD\u5BB6\u5C42\u9762\u7684\u9AD8\u5EA6\u5173\u6CE8\u3002</p><p>\u65E9\u57282007\u5E745\u6708\uFF0C\u300A\u4E2D\u5171\u4E2D\u592E\u56FD\u52A1\u9662\u5173\u4E8E\u52A0\u5F3A\u9752\u5C11\u5E74\u4F53\u80B2\u589E\u5F3A\u9752\u5C11\u5E74\u4F53\u8D28\u7684\u610F\u89C1\u300B\u5DF2\u7ECF\u5370\u53D1\uFF0C\u5176\u4E2D\u63D0\u51FA\u901A\u8FC75\u5E74\u5DE6\u53F3\u7684\u65F6\u95F4\uFF0C\u4F7F\u6211\u56FD\u9752\u5C11\u5E74\u666E\u904D\u8FBE\u5230\u56FD\u5BB6\u4F53\u8D28\u5065\u5EB7\u7684\u57FA\u672C\u8981\u6C42\u2026\u2026\u8FD1\u89C6\u7684\u53D1\u751F\u7387\u660E\u663E\u4E0B\u964D\u3002</p><p>\u4E00\u5E74\u591A\u4E4B\u540E\uFF0C\u6559\u80B2\u90E8\u5236\u5B9A\u300A\u4E2D\u5C0F\u5B66\u5B66\u751F\u8FD1\u89C6\u773C\u9632\u63A7\u5DE5\u4F5C\u65B9\u6848\u300B\uFF0C\u5C31\u4FDD\u62A4\u5B66\u751F\u89C6\u529B\u63D0\u51FA\u5DE5\u4F5C\u63AA\u65BD\uFF0C\u5305\u62EC\u4FDD\u8BC1\u7761\u7720\u3001\u5EFA\u7ACB\u89C6\u529B\u5B9A\u671F\u68C0\u6D4B\u5236\u5EA6\u3001\u575A\u6301\u6BCF\u5929\u4E00\u5C0F\u65F6\u4F53\u80B2\u953B\u70BC\u5236\u5EA6\u7B49\u3002</p><p>\u5085\u6DFB\u5728\u91C7\u8BBF\u4E2D\u5766\u8A00\uFF0C\u4E2D\u592E\u5728\u6559\u80B2\u653F\u7B56\u6587\u4EF6\u4E2D\u4E00\u76F4\u5F3A\u8C03\u5065\u5EB7\u7B2C\u4E00\u7684\u7406\u5FF5\uFF0C\u4F46\u8FD9\u79CD\u7406\u5FF5\u5728\u5404\u5730\u5E76\u672A\u5F97\u5230\u5F88\u597D\u7684\u6267\u884C\uFF0C\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u539F\u56E0\u5728\u4E8E\uFF0C\u653F\u7B56\u672C\u8EAB\u505C\u7559\u5728\u7406\u5FF5\u5F15\u5BFC\u5C42\u9762\uFF0C\u7F3A\u4E4F\u8BE6\u7EC6\u3001\u5177\u4F53\u7684\u5B9E\u65BD\u7EC6\u5219\u548C\u8BC4\u4EF7\u6807\u51C6\u3002</p><p>\u5085\u6DFB\u8BA4\u4E3A\uFF0C\u7F51\u7EDC\u4FE1\u606F\u65F6\u4EE3\u5BF9\u5B69\u5B50\u89C6\u89C9\u7684\u8BF1\u60D1\u662F\u7A7A\u524D\u7684\uFF0C\u7535\u8111\u3001\u624B\u673A\u3001\u6E38\u620F\u673A\u7B49\u7535\u5B50\u8BBE\u5907\u51E0\u4E4E\u968F\u65F6\u968F\u5730\u53EF\u89C1\uFF0C\u7F51\u6E38\u3001\u624B\u6E38\u7B49\u5A31\u4E50\u65B9\u5F0F\u5728\u513F\u7AE5\u9752\u5C11\u5E74\u4E2D\u666E\u904D\u6D41\u884C\uFF0C\u4ED6\u4EEC\u7684\u89C6\u529B\u5065\u5EB7\u9762\u4E34\u524D\u6240\u672A\u6709\u7684\u8BF1\u60D1\u548C\u5A01\u80C1\u3002\u201C\u4E00\u4E9B\u5BB6\u957F\u8FD8\u6CA1\u6709\u5145\u5206\u610F\u8BC6\u5230\u8FD9\u4E9B\u7535\u5B50\u8BBE\u5907\u5BF9\u513F\u7AE5\u89C6\u529B\u7684\u5DE8\u5927\u5A01\u80C1\uFF0C\u7F3A\u4E4F\u5FC5\u8981\u7684\u7BA1\u6559\u548C\u7EA6\u675F\uFF0C\u751A\u81F3\u5BB6\u957F\u81EA\u5DF1\u4E5F\u6C89\u6EBA\u4E8E\u7535\u8111\u3001\u624B\u673A\u548C\u5E73\u677F\u4E4B\u4E2D\uFF0C\u7ED9\u5B69\u5B50\u9020\u6210\u5F88\u574F\u7684\u5F71\u54CD\u3002\u201D</p><p>\u6B64\u5916\uFF0C\u5F53\u524D\u591A\u6570\u4E2D\u5C0F\u5B66\u91CC\u7684\u5065\u5EB7\u6559\u80B2\u6D41\u4E8E\u5F62\u5F0F\uFF0C\u666E\u904D\u5B58\u5728\u7740\u91CD\u89C6\u4E0D\u8DB3\u3001\u5B9E\u65BD\u4E0D\u529B\u7684\u60C5\u51B5\uFF0C\u5BF9\u5065\u5EB7\u6559\u80B2\u7684\u8BA4\u8BC6\u8FC7\u4E8E\u7B80\u5355\uFF0C\u4E0D\u91CD\u89C6\u57F9\u517B\u5B66\u751F\u7684\u5065\u5EB7\u4E60\u60EF\u548C\u5065\u5EB7\u7684\u751F\u6D3B\u6001\u5EA6\u3002</p><p>\u636E\u56FD\u5BB6\u4F53\u80B2\u603B\u5C402010\u5E74\u53D1\u5E03\u7684\u300A\u56FD\u6C11\u4F53\u8D28\u76D1\u6D4B\u516C\u62A5\u300B\u663E\u793A\uFF0C\u6211\u56FD\u5C0F\u5B66\u751F\u8FD1\u89C6\u60A3\u75C5\u7387\u4E3A31.67%\uFF0C\u521D\u4E2D\u751F\u4E3A58.07%\uFF0C\u9AD8\u4E2D\u751F\u4E3A76.02%\u3002\u5230\u4E862014\u5E74\uFF0C\u6559\u80B2\u90E8\u5168\u56FD\u5B66\u751F\u4F53\u8D28\u4E0E\u5065\u5EB7\u8C03\u7814\u7ED3\u679C\u663E\u793A\uFF0C\u4E2D\u56FD\u5C0F\u5B66\u751F\u89C6\u529B\u4E0D\u826F\u68C0\u51FA\u7387\u8FBE\u523045.71%\uFF0C\u521D\u4E2D\u751F\u8FBE\u523074.36%\uFF0C\u9AD8\u4E2D\u751F\u8FBE\u523083.28%\u3002</p><p>\u8FD9\u4E9B\u6570\u636E\u8868\u660E\uFF0C\u6211\u56FD\u9752\u5C11\u5E74\u4F53\u8D28\u5065\u5EB7\u6301\u7EED\u4E0B\u6ED1\u7684\u8D8B\u52BF\u5E76\u672A\u5F97\u4EE5\u6839\u672C\u6539\u53D8\uFF0C\u5B66\u751F\u7684\u8FD1\u89C6\u7387\u7B49\u6307\u6807\u4ECD\u5728\u4E0A\u5347\u3002</p><p><strong>\u89C6\u529B\u5065\u5EB7\u7EB3\u5165\u8003\u6838</strong></p><p><strong>\u76F8\u5173\u6CD5\u5F8B\u6709\u5F85\u5B8C\u5584</strong></p><p>\u8F6C\u6298\u70B9\u51FA\u73B0\u57282015\u5E7410\u6708\u3002\u5F7C\u65F6\uFF0C\u515A\u7684\u5341\u516B\u5C4A\u4E94\u4E2D\u5168\u4F1A\u53EC\u5F00\uFF0C\u5728\u968F\u540E\u53D1\u5E03\u7684\u4F1A\u8BAE\u516C\u62A5\u4E2D\uFF0C\u660E\u786E\u63D0\u51FA\u63A8\u8FDB\u5065\u5EB7\u4E2D\u56FD\u5EFA\u8BBE\u3002</p><p>\u4E00\u5E74\u4E4B\u540E\uFF0C\u4E2D\u5171\u4E2D\u592E\u3001\u56FD\u52A1\u9662\u5370\u53D1\u300A\u201C\u5065\u5EB7\u4E2D\u56FD2030\u201D\u89C4\u5212\u7EB2\u8981\u300B\uFF0C\u63D0\u51FA\u5C06\u5065\u5EB7\u6559\u80B2\u7EB3\u5165\u56FD\u6C11\u6559\u80B2\u4F53\u7CFB\uFF0C\u628A\u5065\u5EB7\u6559\u80B2\u4F5C\u4E3A\u6240\u6709\u6559\u80B2\u9636\u6BB5\u7D20\u8D28\u6559\u80B2\u7684\u91CD\u8981\u5185\u5BB9\u3002</p><p>\u7D27\u63A5\u7740\uFF0C\u56FD\u5BB6\u536B\u751F\u8BA1\u751F\u59D4\u3001\u6559\u80B2\u90E8\u7B49\u636E\u6B64\u8054\u5408\u5370\u53D1\u300A\u5173\u4E8E\u52A0\u5F3A\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u9632\u63A7\u5DE5\u4F5C\u7684\u6307\u5BFC\u610F\u89C1\u300B\u3002\u8FD9\u4EFD\u6307\u5BFC\u610F\u89C1\u660E\u786E\u6307\u51FA\uFF0C\u8FD1\u5E74\u6765\u6211\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u53D1\u751F\u7387\u5448\u4E0A\u5347\u8D8B\u52BF\uFF0C\u4E14\u968F\u5E74\u9F84\u589E\u957F\u6709\u660E\u663E\u589E\u52A0\uFF1B\u8FD1\u89C6\u5DF2\u7ECF\u6210\u4E3A\u5F71\u54CD\u6211\u56FD\u672A\u6765\u56FD\u6C11\u7D20\u8D28\u7684\u4E25\u91CD\u95EE\u9898\u3002</p><p>\u515A\u7684\u5341\u516B\u4EE5\u6765\uFF0C\u6211\u56FD\u5E94\u5BF9\u513F\u7AE5\u9752\u5C11\u5E74\u89C6\u529B\u5371\u673A\u7684\u653F\u7B56\u5C42\u6B21\u65E5\u76CA\u63D0\u9AD8\u3002</p><p>2018\u5E748\u6708\uFF0C\u6559\u80B2\u90E8\u3001\u56FD\u5BB6\u536B\u751F\u5065\u5EB7\u59D4\u5458\u4F1A\u3001\u56FD\u5BB6\u4F53\u80B2\u603B\u5C40\u3001\u8D22\u653F\u90E8\u3001\u4EBA\u529B\u8D44\u6E90\u548C\u793E\u4F1A\u4FDD\u969C\u90E8\u7B49\u516B\u90E8\u95E8\u8054\u5408\u5370\u53D1\u4E86\u300A\u7EFC\u5408\u9632\u63A7\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u5B9E\u65BD\u65B9\u6848\u300B\u3002</p><p>\u4ECA\u5E747\u6708\uFF0C\u300A\u56FD\u52A1\u9662\u5173\u4E8E\u5B9E\u65BD\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8\u7684\u610F\u89C1\u300B\u5370\u53D1\uFF0C\u56FD\u52A1\u9662\u529E\u516C\u5385\u914D\u5957\u5370\u53D1\u300A\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8\u7EC4\u7EC7\u5B9E\u65BD\u548C\u8003\u6838\u65B9\u6848\u300B\uFF0C\u56FD\u5BB6\u5C42\u9762\u6210\u7ACB\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8\u63A8\u8FDB\u59D4\u5458\u4F1A\u5E76\u5370\u53D1\u300A\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8(2019-2030\u5E74)\u300B\u3002</p><p>\u5B9E\u65BD\u4E2D\u5C0F\u5B66\u5065\u5EB7\u4FC3\u8FDB\u884C\u52A8\u6210\u4E3A\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8\u768415\u4E2A\u4E13\u9879\u884C\u52A8\u4E4B\u4E00\uFF0C\u5176\u4E2D\u5305\u62EC\uFF0C\u52302022\u5E74\u548C2030\u5E74\uFF0C\u56FD\u5BB6\u5B66\u751F\u4F53\u8D28\u5065\u5EB7\u6807\u51C6\u8FBE\u6807\u4F18\u826F\u7387\u5206\u522B\u8FBE\u523050%\u53CA\u4EE5\u4E0A\u548C60%\u53CA\u4EE5\u4E0A\uFF0C\u5168\u56FD\u513F\u7AE5\u9752\u5C11\u5E74\u603B\u4F53\u8FD1\u89C6\u7387\u529B\u4E89\u6BCF\u5E74\u964D\u4F4E0.5\u4E2A\u767E\u5206\u70B9\u4EE5\u4E0A\uFF0C\u65B0\u53D1\u8FD1\u89C6\u7387\u660E\u663E\u4E0B\u964D\u3002</p><p>\u300A\u56FD\u52A1\u9662\u5173\u4E8E\u5B9E\u65BD\u5065\u5EB7\u4E2D\u56FD\u884C\u52A8\u7684\u610F\u89C1\u300B\u8FD8\u63D0\u51FA\uFF0C\u5EFA\u7ACB\u7EE9\u6548\u8003\u6838\u8BC4\u4EF7\u673A\u5236\uFF0C\u5F3A\u5316\u5404\u5730\u515A\u59D4\u3001\u653F\u5E9C\u548C\u5404\u6709\u5173\u90E8\u95E8\u7684\u843D\u5B9E\u8D23\u4EFB\u3002\u4ECE\u4ECA\u5E74\u8D77\uFF0C\u6BCF\u5E74\u8FD8\u5C06\u5F00\u5C55\u5404\u7701(\u81EA\u6CBB\u533A\u3001\u76F4\u8F96\u5E02)\u4EBA\u6C11\u653F\u5E9C\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u9632\u63A7\u5DE5\u4F5C\u8BC4\u8BAE\u8003\u6838\u3002</p><p>\u5085\u6DFB\u8BA4\u4E3A\uFF0C\u7EB5\u89C2\u8FD9\u51E0\u5E74\u56FD\u5BB6\u653F\u7B56\u7684\u8D70\u5411\u53EF\u4EE5\u53D1\u73B0\uFF0C\u653F\u7B56\u89C4\u5B9A\u8D8A\u6765\u8D8A\u660E\u786E\u3001\u5177\u4F53\uFF0C\u5177\u6709\u53EF\u64CD\u4F5C\u6027\u3002\u4E0B\u4E00\u6B65\u7684\u5173\u952E\u662F\u5728\u5730\u65B9\u653F\u5E9C\u548C\u5B66\u6821\u4E2D\u843D\u5B9E\u8D23\u4EFB\u4EBA\uFF0C\u628A\u513F\u7AE5\u9752\u5C11\u5E74\u7684\u89C6\u529B\u5065\u5EB7\u60C5\u51B5\u7EB3\u5165\u5BF9\u4E2A\u4EBA\u548C\u5355\u4F4D\u7684\u8003\u6838\u6307\u6807\u91CC\uFF0C\u5E76\u4E14\u660E\u786E\u89C4\u5B9A\u76F8\u5E94\u7684\u5956\u60E9\u63AA\u65BD\uFF0C\u5BF9\u8FBE\u4E0D\u5230\u8981\u6C42\u7684\u4E2A\u4EBA\u548C\u5355\u4F4D\u8981\u8FDB\u884C\u95EE\u8D23\u3002</p><p>\u201C\u5BF9\u5B66\u6821\u800C\u8A00\uFF0C\u5E94\u901A\u8FC7\u7ACB\u6CD5\u6765\u4FDD\u969C\u5B66\u6821\u5728\u5065\u5EB7\u6559\u80B2\u65B9\u9762\u7684\u8D44\u6E90\uFF0C\u89E3\u51B3\u5E08\u8D44\u3001\u8BFE\u65F6\u3001\u6559\u6750\u7B49\u4E25\u91CD\u4E0D\u8DB3\u7684\u95EE\u9898\uFF1B\u5BF9\u5BB6\u5EAD\u800C\u8A00\uFF0C\u4E9F\u987B\u51FA\u53F0\u5BB6\u5EAD\u6559\u80B2\u6CD5\uFF0C\u660E\u786E\u843D\u5B9E\u5BB6\u957F\u5728\u5B69\u5B50\u7684\u8EAB\u5FC3\u5065\u5EB7\u53D1\u5C55\u4E0A\u6240\u62C5\u8D1F\u7684\u6CD5\u5F8B\u8D23\u4EFB\uFF0C\u4ECE\u800C\u5728\u7EA6\u675F\u5BB6\u957F\u7684\u6559\u80B2\u884C\u4E3A\u65F6\u80FD\u6709\u6CD5\u53EF\u4F9D\u3002\u201D\u5085\u6DFB\u8BF4\u3002</p><p>\u5218\u70AB\u9E9F\u540C\u6837\u8BA4\u4E3A\uFF0C\u9632\u6CBB\u548C\u6539\u5584\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\uFF0C\u4E00\u65B9\u9762\u5BB6\u957F\u3001\u5B66\u6821\u3001\u653F\u5E9C\u7B49\u5E94\u8BE5\u8D2F\u5F7B\u6267\u884C\u56FD\u5BB6\u6709\u5173\u653F\u7B56\uFF0C\u53E6\u4E00\u65B9\u9762\u4E5F\u9700\u8981\u76F8\u5173\u5404\u65B9\u79EF\u6781\u53C2\u4E0E\u3002</p><p>\u6B64\u5916\uFF0C\u5218\u70AB\u9E9F\u8FD8\u5EFA\u8BAE\uFF0C\u4ECE\u6CD5\u6CBB\u7684\u89D2\u5EA6\u770B\uFF0C\u4E9F\u987B\u5236\u5B9A\u57FA\u672C\u533B\u7597\u536B\u751F\u4E0E\u5065\u5EB7\u4FC3\u8FDB\u6CD5\uFF0C\u4FEE\u8BA2\u300A\u5B66\u6821\u536B\u751F\u5DE5\u4F5C\u6761\u4F8B\u300B\u7B49\u6CD5\u5F8B\u6CD5\u89C4\uFF0C\u4F9D\u6CD5\u660E\u786E\u76F8\u5173\u5404\u65B9\u7684\u6CD5\u5F8B\u8D23\u4EFB\uFF0C\u6539\u53D8\u5F53\u524D\u513F\u7AE5\u9752\u5C11\u5E74\u8FD1\u89C6\u7684\u4E25\u91CD\u5C40\u9762\u3002</p><p>\uFF08\u8BB0\u8005 \u9648\u78CA\uFF09</p>',52),s=[n];function a(_,i,c,l,d,h){return e(),t("div",null,s)}const f=p(o,[["render",a]]);export{m as __pageData,f as default};
